    {
      Repr := {this};  
    }
    constructor Init'()
    {
      Rapr := {this};
    }
    constructor Create()
    {
      S := {this};  
    }
    constructor Create'()
    {
      T := {this};
    }
    constructor Two()
    {
      new;
      var ll: LinkedList? := this;
      var o: object? := this;
      if
      case true =>  T := {o};
      case true =>  S := {o};
      case true =>  Repr := {ll};
      case true =>  Rapr := {ll};
      case true =>  S := {ll};
      case true =>  T := {ll};
    }
