method benda(L:array<int>, v0:int, v1:int) returns (x:int, y:int)
	requires L != null;
	requires forall i::0 <= i < L.Length ==> 0 <= L[i] < L.Length; 
	requires forall i,j::0 <= i < j < L.Length ==> L[i] != L[j]; 
	requires v0 !in L[..] && v1 !in L[..] && v0 != v1;
	modifies L; 
	ensures forall j::0 <= j < L.Length ==> L[j] == j; 
	ensures x == v0 && y == v1; 
{
	var i;
	i,x,y := 0,v0,v1;
	while (i < L.Length)
		invariant 0 <= i <= L.Length;
		invariant forall j::i <= j < L.Length ==> i <= L[j] < L.Length; 
		invariant forall j,k::i <= j < k < L.Length ==> L[j] != L[k]; 
		invariant forall j::0 <= j < i ==> L[j] == j; 
		invariant {x,y} == {v0,v1}; 
	{
		if (L[i] != i) {    
			x,L[i] := L[i],x;
			x := cycle(L,i,x,(set z | i < z < L.Length && L[z] != z));
				y,L[x] := L[x],y;
				x,L[x] := L[x],x;
				y,L[i] := L[i],y;      
		}
		i := i+1;
	}
	if (x != v0) {
		x,y := y,x;
	} 
}
method cycle(L:array<int>, i:int, a:int, s:set<int>) returns (x:int)
	requires L!=null;
	requires 0 <= i < a < L.Length; 
	requires forall j::i < j < L.Length ==> i <= L[j] < L.Length; 
	requires forall j::i < j < L.Length ==> L[j] != a; 
	requires forall j,k::i < j < k < L.Length ==> L[j] != L[k]; 
	requires s == (set z | i < z < L.Length && L[z] != z); 
	modifies L;
	decreases s;
	ensures forall j::0 <= j <= i ==> L[j] == old(L[j]); 
	ensures forall j::i < j < L.Length ==> i <= L[j] < L.Length; 
	ensures forall j::i < j < L.Length ==> L[j] != x; 
	ensures forall j,k::i < j < k < L.Length ==> L[j] != L[k]; 
	ensures i <= x < L.Length; 
	ensures L[x] == i;
{
	x := a;
	if (L[x] != i) {
		x,L[x] := L[x],x;
		x:=cycle(L,i,x,s-{a});
	}
}
