function More(x: int): int {
  if x <= 0 then 1 else More(x - 2) + 3
}
lemma {:induction false} Increasing(x: int)
  ensures x < More(x)
{
  if x <= 0 {
    calc {
      x;
    <=  { assert x <= 0; }
      0;
    <  
      1;
    ==  
      More(x);
    }
  } else {
    calc {
      x < More(x);
    <==  
      x + 1 < More(x);
    ==  
      x + 1 < More(x - 2) + 3;
    ==  
      x - 2 < More(x - 2);
    <==  { Increasing(x - 2); }
      true;
    }
  }
}

method below_zero(operations: seq<int>) returns (s:array<int>, result:bool)
{
  result := false;
  s := new int[|operations| + 1];
  var i := 0;
  s[i] := 0;
  while i < s.Length
    invariant 0 <= i <= s.Length
    invariant s[0]==0
    invariant s.Length == |operations| + 1
    invariant forall x :: 0 <= x < i-1 ==> s[x+1]==s[x]+operations[x]
  {
    if i>0{
        s[i] := s[i - 1] + operations[i - 1];
    }
    i := i + 1;
  }
  i:=0;
  while i < s.Length
    invariant 0 <= i <= s.Length
    invariant forall x :: 0 <= x < i ==> s[x] >= 0
  {
    if s[i] < 0 {
      result := true;
      return;
    }
    i := i + 1;
  }
}

method reverse<T>(input: seq<T>) returns (result: seq<T>)
  requires |input| > 0
  ensures |input| == |result| 
  ensures forall x | 0 <= x < |input| :: input[x] == result[(|result| - 1 - x)] 
  {
  var final : seq<T> := [];
  var i := |input| - 1;
  while i >= 0
    invariant -1 <= i < |input|
    invariant |input| - 1 == |final| + i
    invariant forall x | 0 <= x < |input| - 1 - i :: input[(|input| - 1 - x)] == final[x]
    {
    final := final + [input[i]];
    i := i - 1;
  }
  return final;
}

