requires  0 <= i <= j < |q|
requires i <= i_result
requires j_result <= j
requires Sorted(q)
requires 0 <= i_result < j_result < |q|
requires q[i_result] + q[j_result] == x
ensures Inv(q, x, i, j, i_result, j_result)
