type Process(==) = int  
datatype CState = Thinking | Hungry | Eating  
class TicketSystem
{
  var ticket: int  
  var serving: int  
  const P: set<Process>  
  var cs: map<Process, CState>  
  var t: map<Process, int>  
  predicate Valid()
    reads this  
  {
    && cs.Keys == t.Keys == P  
    && serving <= ticket
    && (forall p ::  
      p in P && cs[p] != Thinking
      ==> serving <= t[p] < ticket
    )
    && (forall p, q ::  
      p in P && q in P && p != q && cs[p] != Thinking && cs[q] != Thinking
      ==> t[p] != t[q]
    )
    && (forall p ::  
      p in P && cs[p] == Eating
      ==> t[p] == serving
    )
  }
  constructor (processes: set<Process>)
    ensures Valid()  
    ensures P == processes  
  {
    P := processes;
    ticket, serving := 0, 0;  
    cs := map p | p in processes :: Thinking;  
    t := map p | p in processes :: 0;
  }
  method Request(p: Process)
    requires Valid() && p in P && cs[p] == Thinking  
    modifies this  
    ensures Valid()  
  {
    t, ticket := t[p := ticket], ticket + 1;  
    cs := cs[p := Hungry];  
  }
  method Enter(p: Process)
    requires Valid() && p in P && cs[p] == Hungry  
    modifies this  
    ensures Valid()  
  {
    if t[p] == serving  
    {
      cs := cs[p := Eating];  
    }
  }
  method Leave(p: Process)
    requires Valid() && p in P && cs[p] == Eating  
    modifies this  
    ensures Valid()  
  {
    serving := serving + 1;  
    cs := cs[p := Thinking];  
  }
  lemma MutualExclusion(p: Process, q: Process)
    requires Valid() && p in P && q in P
    requires cs[p] == Eating && cs[q] == Eating
    ensures p == q
  {
  }
}
method Run(processes: set<Process>)
  requires processes != {}  
  decreases *  
{
  var ts := new TicketSystem(processes);
  var schedule := [];  
  var trace := [(ts.ticket, ts.serving, ts.cs, ts.t)];  
  while true
    invariant ts.Valid()
    decreases *  
  {
    var p :| p in ts.P;  
    match ts.cs[p] {
      case Thinking => ts.Request(p);
      case Hungry => ts.Enter(p);
      case Eating => ts.Leave(p);
    }
    schedule := schedule + [p];
    trace:=trace + [(ts.ticket, ts.serving, ts.cs, ts.t)];
  }
}
method RunFromSchedule(processes: set<Process>, schedule: nat -> Process)
  requires processes != {}
  requires forall n :: schedule(n) in processes
  decreases *
{
  var ts := new TicketSystem(processes);
  var n := 0;
  while true
    invariant ts.Valid()
    decreases *  
  {
    var p := schedule(n);
    match ts.cs[p] {
      case Thinking => ts.Request(p);
      case Hungry => ts.Enter(p);
      case Eating => ts.Leave(p);
    }
    n := n + 1;
  }
}

function power(b: nat, e: nat): nat 
decreases e
{
    if e == 0 then 1 else b * power(b,e-1)
}
method expoBySquare(x:nat,n:nat) returns (p:nat)
requires n>=0
ensures p == power(x,n)
decreases *
{
    var N,q := n,x;
    p := 1;
    while N>=0
    decreases *
    {
        p := if N%2 == 0 then p else p*q;
        q := q * q;
        N := if N%2 == 0 then N/2 else (N-1)/2;
    }
}

