method Find(a: array<int>, key: int) returns (index: int)
  ensures -1<=index<a.Length
  ensures index!=-1 ==> a[index]==key
  ensures index == -1 ==> (forall i::0 <= i < a.Length ==> a[i] != key)
{
  var i := 0;
  while i < a.Length
    invariant 0 <= i <= a.Length
    invariant forall j :: 0 <= j < i ==> a[j] != key
  {
    if a[i] == key {
      return i;
    }
    i := i + 1;
  }
  return -1;
}

method add_by_inc(n: nat, m: nat) returns (r: nat)
  ensures r == m + n;
{
  r := n;
  var i := 0;
  while i < m
    invariant i <= m;
    invariant r == n + i;
    decreases m - i;
  {
    i := i + 1;
    r := r + 1;
  }
  return r;
}
method add_by_inc_Vc(n: nat, m: nat) returns (r: nat)
{
  assume(true);
  r := n;
  var i := 0;
  assert(i <= m);
  assert(r == n + i);
  i, r := *, *;
  assume(i <= m);
  assume(r == n + i);
  if (i < m)
  {
    ghost var rank0 := (m - i);
    i := i + 1;
    r := r + 1;
    assert(i <= m);
    assert(r == n + i);
    ghost var rank := (m - i);
    assert(rank < rank0);
    assert(rank >= 0);
    assume(false);
  }
  assert r == m + n;
}
method Product(m: nat, n: nat) returns (res: nat)
  ensures res == m*n;
{
  var m1: nat := m;
  res := 0;
  while (m1!=0)
    invariant m1 >= 0;
    invariant res == (m - m1) * n;
    decreases m1;
  {
    var n1: nat := n;
    while (n1 != 0)
      invariant n1 >= 0;
      invariant res == (m - m1) * n + (n-n1);
      decreases n1;
    {
      res := res+1;
      n1 := n1-1;
    }
    m1 := m1-1;
  }
}
method Product_Vc(m: nat, n: nat) returns (res: nat)
{
  assume(true);
  var m1: nat := m;
  res := 0;
  assert(m1 >= 0 && res == (m - m1) * n);
  res, m1 := *, *;
  assume(m1 >= 0 && res == (m - m1) * n);
  if (m1 != 0)
  {
    var rankM0 := m1;
    var n1: nat := n;
    assert(n1 >= 0 && res == (m - m1) * n + (n - n1));
    res, n1 := *, *;
    assume(n1 >= 0 && res == (m - m1) * n + (n - n1));
    if (n1 != 0)
    {
      ghost var rankN0 := n1;
      res := res+1;
      n1 := n1-1;
      assert(n1 >= 0 && res == (m - m1) * n + (n - n1));
      ghost var rankN := n1;
      assert(rankN < rankN0 && rankN >= 0);
      assume(false);
    }
    m1 := m1-1;
    assert(m1 >= 0 && res == (m - m1) * n);
    var rankM := m1;
    assert(rankM < rankM0 && rankM >= 0);
    assume(false);
  }
  assert(res == m*n);
}

predicate member(s: seq<int>, n: int) {
	exists i :: 0 <= i <|s| && s[i] == n
}
method linsearch(s: seq<int>, n: int) returns (found:bool, ind:int)
	ensures !found ==> !member(s, n)										
	ensures  found ==>  member(s, n)
    ensures ind <= |s|										
{
	ind := 0;
	found := false;
	while(ind < |s| && !found)
		decreases |s|-ind + (if found then 0 else 1)							
		invariant 0 <= ind <= |s| 
		invariant !member(s[..ind],n)										
		invariant found ==> member(s,n)									
	{
		if (s[ind] == n) { 
			found := true;
		} else {
			ind := ind + 1;
		}
	}
}
