		decreases |s|-ind + (if found then 0 else 1);						
		invariant 0 <= ind <= |s|
		invariant !member(s[..ind],n)
		invariant found ==> member(s,n)
	{
		if (s[ind] == n) { 
			found := true;
		} else {
			ind := ind + 1;
		}
	}
}

datatype Tree = Empty | Node(int,Tree,Tree)
method Main() {
	var tree := Node(5, Node(2, Empty, Empty), Node(8, Node(7, Empty, Empty), Node(8, Empty, Empty)));
	assert NumbersInTree(tree) == multiset([2,5,7,8,8]);
	assert NumbersInTree(tree)[2] == 1; 
	assert NumbersInTree(tree)[5] == 1; 
	assert NumbersInTree(tree)[7] == 1; 
	assert NumbersInTree(tree)[8] == 2; 
	assert NumbersInTree(tree)[9] == 0; 
	assert BST(tree);
	var count8 := CountOccurrences(tree, 8);
	print "The number of occurrences of 8 in the tree ";
	print tree;
	print " is ";
	print count8;
	assert count8 == 2;
}
function NumbersInTree(t: Tree): multiset<int>
decreases t
{
	match t {
		case Empty => multiset([])
		case Node(n,l,r) => multiset([n])+NumbersInTree(l)+NumbersInTree(r)
	}
}
predicate BST(t: Tree)
decreases t
{
	match t {
		case Empty => true
		case Node(n,l,r) =>
			BST(l) && BST(r) &&
			(forall x :: x in NumbersInTree(l) ==> x < n) &&
			(forall x :: x in NumbersInTree(r) ==> x >= n)
	}
}
method RecEffective(tree: Tree, key: int) returns (count: nat)
	requires BST(tree)
	decreases tree
	ensures count == NumbersInTree(tree)[key]
  {
  var leftAcc := 0;
  var rightAcc := 0;
	match tree {
	case Empty =>
		assert tree == Empty;
		assert 0 == NumbersInTree(tree)[key];
		count := 0;
		assert count == NumbersInTree(tree)[key];
	case Node(val,left,right) =>
		assert tree == Node(val,left,right);
		assert left < tree;
		assert right < tree;
    if key < val{
      leftAcc := RecEffective(left, key);
    }else{
			rightAcc:= RecEffective(right, key);
    }
		assert (if val == key then 1 else 0) +  leftAcc + rightAcc == NumbersInTree(tree)[key];
    count := (if val == key then 1 else 0) +  leftAcc + rightAcc;
		assert count == NumbersInTree(tree)[key];
	}
	assert count == NumbersInTree(tree)[key];
}
method CountOccurrences(t: Tree, key: int) returns (count: nat)
	requires BST(t)
	ensures count == NumbersInTree(t)[key]
  {
		count := RecEffective(t, key);
  }

function sum(a:seq<int>, i:int, j:int) : int 
	requires 0 <= i <= j <=|a|
{
	if i == j then 0
	else sum(a, i, j-1) + a[j-1]
}
predicate lowsum(a: seq<int>, k:int, s: int)
	requires 0 <= k <= |a|
{
	forall i, j  :: 0 <= i < j <= k ==> s <= sum(a,i,j)
}
predicate lowsumend(a: seq<int>, k:int, t: int)
	requires 0 <= k <= |a|
 {
	forall i :: 0 <= i < k ==> t <= sum(a,i,k)
}
predicate issum(a: seq<int>, k:int, s:int)
	requires 0 <= k <= |a|
{
	exists i,j :: 0 <= i < j <= k && s == sum(a,i,j)
}
predicate issumend(a: seq<int>, k:int, t:int)
	requires 0 <= k <= |a|
{
	exists i:: 0 <= i < k && t == sum(a,i,k)
}
lemma issumendL(a: seq<int>, k:int, t:int)
	requires 0 < k < |a|
	requires issumend(a,k,t)
	ensures issumend(a,k+1,t+a[k])
{
	assert exists i :: 0 <= i < k && t == sum(a,i,k);
	assert exists i :: 0 <= i < k && t+a[k] == sum(a,i,k) + a[k];
	assert forall i :: 0 <= i < k ==> sum(a,i,k) + a[k] == sum(a,i,k+1);
}
method minsum(a: seq<int>) returns (ret:int)
	requires |a| > 0
	ensures lowsum(a,|a|,ret)
	ensures issum(a,|a|,ret)				
{
	var k := 1;
	var t := a[0];
	var s := a[0];
	assert s == sum(a,0,1);					
	while (k != |a|)
		decreases |a| - k					
		invariant 0 < k <= |a|
		invariant lowsumend(a,k,t)			
		invariant lowsum(a,k,s)				
		invariant issumend(a,k,t)			
		invariant issum(a,k,s)				
	{
		if (t + a[k] < a[k]) {
			assert issumend(a,k,t);			
			issumendL(a,k,t);				
			t := t + a[k];
			assert issumend(a,k+1,t);		
		} else {
			t := a[k];
			assert t == sum(a,k, k+1);		
		}
		if (t < s) {
			s := t;
		}
		k := k + 1;
	}
	return s;
}

