method Factorial (n: nat) returns (p: nat)
requires n >= 0;
{
    var i: int;
    i, p := 1, 1;
    while (i <= n)
    invariant i <= n + 1;
    invariant p <= p * i;
    decreases n + 1 - i;
    {
        p := p * i;
        i := i + 1;
    }
}
