function Fib (n : int) : int
{
	if (n < 2) then n
	else Fib(n-2) + Fib(n-1)
}
method Fibonacci (n : int) returns (f : int)
requires n >= 0
ensures f == Fib(n)  
{
	var aux := 0;
	if(n<2){f:=n;}
	else{
		var i := 0;
		f := 1;
		while(i <= n-2)
		invariant 0 <= i <= n-1
		invariant f == Fib(i+1)
		invariant aux == Fib(i)
		{
			f := f + aux;
			aux := f - aux;
			i := i + 1;
		}
	}
}

