method addDonor(donors: seq<array<int>>, newDonor : array<int>)  returns (updated: seq <array<int>>)
requires newDonor != null;
requires donors != [] && |donors| >= 0;
requires forall r : int :: (0 <= r < |donors| ==> donors[r] != null);
requires forall r : int :: (0 <= r < |donors| ==> donors[r] != newDonor);
requires forall r : int :: (0 <= r < |donors| ==> donors[r].Length == 7);
requires newDonor.Length == 7;
ensures forall r : int :: (0 <= r < |donors| ==> (donors[r] in updated && newDonor in updated));
ensures forall k : int :: (0 <= k < |updated|  ==> (updated[k] != null));
{   
    updated := [];
    assert updated == [];
    var i := 0;
    updated := [];
    while(i < |donors|)
    invariant 0 <= i <= |donors|
    invariant forall k : int :: (0 <= k < i ==> donors[k] in updated)
    invariant forall k : int :: (0 <= k < |updated|  ==> (updated[k] != null))
    {
        assert donors[i] != null;
        updated := updated + [donors[i]];
        assert updated[|updated|-1] != null;
        i := i + 1;
    }
    updated := updated + [newDonor];   
}

predicate isSorted(a: array<int>, start:int, end:int)
requires a != null 
requires 0<=start<=end<=a.Length
reads a
{ 
    forall x, y:: start<=x<y<end ==> a[x]<=a[y] 
}
method InsertionSort(toSort: array<int>)
requires toSort != null 
requires toSort.Length > 1
ensures isSorted(toSort, 0, toSort.Length);
ensures multiset(toSort[..]) == multiset(old(toSort[..]));
modifies toSort;
{
    var sortedUpTo:=1;
    while (sortedUpTo < toSort.Length)
    invariant 1 <= sortedUpTo <= toSort.Length;
    invariant isSorted(toSort, 0, sortedUpTo);
    invariant multiset(toSort[..]) == multiset(old(toSort[..]));
    {
        var start := sortedUpTo; 
        while (start >= 1 && toSort[start-1] > toSort[start])
        invariant 0 <= start <= sortedUpTo;
        invariant forall i,j:: (0<=i<j<=sortedUpTo && j!=start) ==> toSort[i]<=toSort[j];
        invariant multiset(toSort[..]) == multiset(old(toSort[..]));
        {
            toSort[start-1], toSort[start] := toSort[start], toSort[start-1];
            start:=start-1;
        }
        sortedUpTo:=sortedUpTo+1;
    }
}

