module InsertionSort {
    function insert(a: int, xs: seq<int>): seq<int>
    {
        if xs == [] then
            [a]
        else if a <= xs[0] then
            [a] + xs
        else
            [xs[0]] + insert(a, xs[1..])
    }
    function sort(xs: seq<int>): seq<int>
    {
        if xs == [] then
            []
        else
            insert(xs[0], sort(xs[1..]))
    }
    predicate sorted(xs: seq<int>)
    {
        forall i | 0 <= i < |xs|-1 :: xs[i] <= xs[i+1]
    }
    function insert_sorted(a: int, xs: seq<int>): ()
        requires sorted(xs)
        ensures sorted(insert(a, xs))
    {
        if xs == [] then
            ()
        else if a <= xs[0] then
            ()
        else
            insert_sorted(a, xs[1..])
    }
    function sort_sorted(xs: seq<int>): ()
        ensures sorted(sort(xs))
    {
        if xs == [] then
            ()
        else
            var _ := sort_sorted(xs[1..]);
            insert_sorted(xs[0], sort(xs[1..]))
    }
}

method Swap(X: int, Y: int) {
  var x, y := X, Y;
  var tmp := x;
  x := y;
  y := tmp;
  assert x == Y && y == X;
}
method SwapArithetic(X: int, Y: int) {
  var x, y := X, Y;
  x := y - x;
  y := y - x;
  x := y + x;
  assert x == Y && y == X;
}
method SwapBitvectors(X: bv8, Y: bv8) {
  var x, y := X, Y;
  x := x ^ y;
  y := x ^ y;
  x := x ^ y;
  assert x == Y && y == X;
}
method SwapSimultaneous(X: int, Y: int) {
  var x, y := X, Y;
  x, y := y, x;
  assert x == Y && y == X;
}

module ConcDB
{
  predicate Param<T>(N:nat, X:array<T>)
  {
    X != null && X.Length == N
  }
  predicate Eq(x:int, y:int) { x == y }
  method ChooseWL(P:set<nat>, WL:set<nat>) returns (i:nat)
    ensures (i in WL && i in P);
  {
    i := *;
    assume (i in WL && i in P);
  }
  method ConcDBSeq
    ( Cs : set<nat>
    )
    decreases *
  {
    var PC      : map<nat, nat> := map i | i in Cs :: 0;
    var X       : map<nat, int> := map i | i in Cs :: 0;
    var V       : map<nat, int> := map i | i in Cs :: 0;
    var Status  : map<nat, int> := map i | i in Cs :: 0;
    var Vp      : map<nat, int> := map i | i in Cs :: 0;
    var DbID : nat;
    var DbMsgType : nat;
    var DbMsgKey : int;
    var DbMsgVal : int;
    var DbResponse : int;
    var DbDb : map<int,int> := map[];
    var ClientWL := Cs;
    while ClientWL != {}
    invariant (Cs == old(Cs))
    invariant (forall c :: c in ClientWL ==> c in Cs)
    invariant (forall c :: c in PC <==> c in Cs)
    invariant (forall c :: c in X  <==> c in Cs)
    invariant (forall c :: c in V  <==> c in Cs)
    invariant (forall c :: c in Vp <==> c in Cs)
    invariant (forall c :: c in Status <==> c in Cs)
    invariant (forall c :: forall k :: c in Cs && Eq(k,X[c]) ==> (
                 (PC[c] == 0)
               || (PC[c] == 1 && Status[c] != 4  && k in DbDb)
               || (PC[c] == 1 && Status[c] == 4 && k in DbDb && DbDb[k] == V[c])
               || (PC[c] == 2 && Status[c] == 4 && k in DbDb && V[c] == Vp[c] && DbDb[k] == Vp[c])
               )
            )
      decreases *
    {
      var c := ChooseWL(Cs, ClientWL);
      if PC[c] == 0
      {
        var k    := *;
        var v    := *;
        X        := X[c := k];
        V        := V[c := v];
        DbID     := c;
        DbMsgKey := X[c];
        DbMsgVal := V[c];
        if DbMsgKey in DbDb {
          DbResponse := 3;
        } else {
          DbDb := DbDb[DbMsgKey := DbMsgVal];
          DbResponse := 4;
        }
        Status := Status[c := DbResponse];
        PC     := PC[c := 1];
      } else if PC[c] == 1 {
        if (Status[c] == 4) {
          DbMsgType := 1;
          DbMsgKey := X[c];
          if DbMsgKey in DbDb {
            DbResponse := DbDb[DbMsgKey];
          } else {
            DbResponse := *;
          }
          Vp := Vp[c := DbResponse];
          PC := PC[c := 2];
          ClientWL := ClientWL - {c};
          assert (Vp[c] == V[c]);
        }
      }
    }
  }
}

module product_sol{ 
method CalcProduct(m: int, n: int)returns (res:int)
	requires n > 0
	requires m > 0
	ensures res == m*n{
	var m1 := m;
	res := 0;
	while m1 != 0 
		invariant res == (m-m1)*n{
		var n1 := n;
		ghost var old_res := res;
		while n1 != 0 
			invariant res == old_res + (n-n1);{
			res := res + 1;
			n1 := n1 - 1;
		}
		m1 := m1 - 1;
	}
	return;
}
}

