method triangle(n:int) returns (t:int)
  requires n >=0
  ensures t == n * (n+1) / 2 
{
  t := 0;
  var i := 0;
  while i < n
    invariant i <= n
    invariant t == i * (i+1) / 2
    decreases n - i
  {
    i := i+1;
    t := t+i;
  }
  return t;                         
}
