method maxarray (A:array<int>) returns (r:int) 
  requires A.Length > 0
  ensures contains(A, r)
  ensures forall j :: 0 <= j < A.Length ==> r >= A[j]
{
  r := A[0];
  var i := 1;
  while i < A.Length 
    invariant 1 <= i <= A.Length
    invariant contains_upto(A, i, r)
    invariant forall j :: 0 <= j < i ==> r >= A[j]
    decreases A.Length - i
  {
    if r < A[i] {
      r := A[i];
    }
    i := i+1;
  }
  assert i == A.Length;
}
