module {:extern} Options {
  datatype Option<V> = None | Some(value:V)
  function MapOption<V0, V1>(opt: Option<V0>, f: V0 ~> V1) : (result: Option<V1>)
  requires opt.Some? ==> f.requires(opt.value)
  ensures opt.Some? <==> result.Some?
  ensures result.Some? ==> result.value == f(opt.value)
  reads if opt.Some? then f.reads(opt.value) else {}
  {
    match opt {
      case Some(v) => Some(f(v))
      case None => None
    }
  }
  function FlatMapOption<V0, V1>(opt: Option<V0>, f: V0 ~> Option<V1>) : (result: Option<V1>)
  requires opt.Some? ==> f.requires(opt.value)
  ensures opt.Some? && f(opt.value).Some? ==> result.Some?
  ensures opt.Some? && f(opt.value).Some? ==> result.value == f(opt.value).value
  reads if opt.Some? then f.reads(opt.value) else {}
  {
    match opt {
      case Some(v) => f(v)
      case None => None
    }
  }
}

function More(x: int): int {
  if x <= 0 then 1 else More(x - 2) + 3
}
lemma {:induction false} Increasing(x: int)
  ensures x < More(x)
{
  if x <= 0 {
    assert More(x) == 1; 
  } else {
    assert More(x) == More(x - 2) + 3; 
    Increasing(x - 2); 
    assert More(x) == More(x - 2) + 3 &&
           x - 2 < More(x - 2);
    assert More(x) == More(x - 2) + 3 &&
           x + 1 < More(x - 2) + 3;
    assert x + 1 < More(x);
  }
}

method SearchRecursive( a: seq<int>, i: int, j: int, x: int ) returns (k: int)
    decreases j-i;
    requires 0 <= i <= j <= |a|;
    ensures i <= k < j || k == -1;
    ensures k != -1 ==> a[k] == x;
    ensures k != -1 ==> forall r | k < r < j :: a[r] != x;
    ensures k == -1 ==> forall r | i <= r < j :: a[r] != x;
{
    if j == i
    {
        k := -1;
        return;
    }
    if a[j-1] == x
    {
        k := j-1;
        return;
    }
    else
    {
        k := SearchRecursive(a, i, j-1, x);
    }
}
method SearchLoop( a: seq<int>, i: int, j: int, x: int ) returns (k: int)
    requires 0 <= i <= j <= |a|;
    ensures i <= k < j || k == -1;
    ensures k != -1 ==> a[k] == x;
    ensures k != -1 ==> forall r | k < r < j :: a[r] != x;
    ensures k == -1 ==> forall r | i <= r < j :: a[r] != x;
{
    if i == j
    {
        return -1;
    }
    var t := j;
    while t > i
        decreases t;
        invariant forall p | t <= p < j :: a[p] != x; 
    {
        if a[t-1] == x
        {
            k := t-1;
            return;
        }
        else 
        {
            t := t - 1;
        }
    }
    k := -1;
}

predicate just1(a:array<int>,key:int)
   reads a
   requires a != null
{
  ((exists k | 0 <= k < a.Length :: a[k] == key) && !(exists k,j | 0 <= k < j < a.Length :: a[k] == key && a[j] == key))
}
method Just1(a: array<int>, key: int) returns (b: bool)
requires a != null
ensures just1(a,key) == b
{
    var count := 0;
    var i := 0;
    b:=false;
    if(a.Length == 0){
        return;
    }
    while i < a.Length
    invariant 0 <= i <= a.Length;
    invariant !(exists k | 0 <= k < i :: a[k] == key)
    decreases a.Length - i
    {
        if a[i] == key 
        {
            var next := i+1;
            assert (next == i+1) && (next > i);
            assert a[i] == key ==> exists j | 0 <= j <= i :: a[j] == key;
            if(next == a.Length){
                assert ((a[a.Length - 1] == key) && (forall k :: 0 <= k < (a.Length - 1) ==> a[k] != key));
                b := true;
                return;
            }
            while next < a.Length
            invariant a[i] == key
            invariant i+1 <= next <= a.Length;
            invariant forall k:: i+1<= k < next ==> a[k] != key
            decreases a.Length - next
            {
                if(a[next] == key){
                   assert forall k :: 0 <= k < i ==> a[k] != key && a[i] == key && !(forall j ::0<= i < j < a.Length ==> a[i] == key ==> a[j] != key);
                   assert exists  k,j | 0 <= k < j < a.Length :: a[k] == key ==> a[j] == key;
                   b:= false;
                   return;
                }
                next := next+1;
            }
            assert forall k :: (i + 1) <= k < a.Length ==> a[k] != key;
            assert a[i] == key && forall m :: 0 <= m < i ==> a[m] != key;
            b:= true;
            return;
        }
        i:=i+1;
    }
    assert i == a.Length;
    assert forall k :: 0<= k < a.Length ==> a[k] != key;
    assert !(exists k | 0 <= k < a.Length :: a[k] == key);
    b:= false;
    return;
}
