function total(a: array<bool>, i:int, key: bool): int
requires a != null;
reads a;
{
	if i < 0
  		then 0
 	else if (a.Length == 0)
  		then 0
  	else if 0<=i<a.Length && a[i] == key
  		then total(a, i-1, key) + 1
  	else total(a, i-1, key)
}
predicate compare(a: array<bool>, a1: array<bool>)
reads a;
reads a1;
requires a != null;
requires a1 != null;
{
	forall key:bool :: total(a, a.Length, key) == total(a1, a1.Length, key)
}
method TwoWaySort(a:array<bool>)
  requires a != null;
  modifies a;
  ensures compare(a, old(a)); 
  ensures forall k:: forall l:: 0 <= k < l < a.Length ==> !a[k] || a[l]; 
{
	var i:int := 0;
	var j:int := a.Length - 1;
	while (i <= j)
	invariant 0 <= i <= (j+1);
	invariant (i-1) <= j < a.Length;	
	decreases (j+1) - i;
	decreases j - (i-1);    
    invariant forall k:: 0 <= k < i ==> !a[k];
    invariant forall l:: j < l < a.Length ==> a[l];
	{
		if a[i] == false 
		{
			i := i + 1;
		}
		else if a[j] == true 
		{
			j := j - 1;
		}
		else
		{
		    var t:bool := a[i];
	        a[i] := a[j];
    	    a[j] := t;
			i := i + 1;
			j := j - 1;
		}
	}
}
method Main() 
{
  var a := new bool[3];
  a[0] := false;
  a[1] := true;
  a[2] := false;
  TwoWaySort(a);
  var i:int := 0;
  while (i < a.Length)
  {
    print a[i];
    i := i + 1;
  }
}

