function sum(ns: seq<int>): int {
    if ns == [] then 0 else sum(ns[..|ns| - 1]) + ns[|ns| - 1]
}
method Sum(ns: array<int>) returns (result: int)
ensures result == sum(ns[..]);
{
    var i := 0;
    result := 0;
    while i < ns.Length
        invariant 0 <= i <= ns.Length && result == sum(ns[..i])
    {
        assert sum(ns[..i + 1]) == sum(ns[..i + 1][..i]) + ns[..i + 1][i];
        assert ns[..i + 1][..i] == ns[..i];
        assert ns[..i + 1][i] == ns[i];
        result := result + ns[i];
        i := i + 1;
    }
    assert ns[..i] == ns[..];
}

method CountLessThan(numbers: set<int>, threshold: int) returns (count: int)
  ensures count == |set i | i in numbers && i < threshold|
{
  count := 0;
  var shrink := numbers;
  var grow := {};
  while |shrink | > 0
    decreases shrink
    invariant shrink + grow == numbers
    invariant grow !! shrink
    invariant count == |set i | i in grow && i < threshold|
  {
    var i: int :| i in shrink;
    shrink := shrink - {i};
    var grow' := grow+{i};
    assert (set i | i in grow' && i < threshold) ==
           (set i | i in grow && i < threshold )+ if i < threshold then {i} else {};
    grow := grow + {i};
    if i < threshold {
      count := count + 1;
    }
  }
}

