method Q3(n0 : int, m0 : int) returns (res : int)
    ensures n0 * m0 == res;
{
    var n, m : int;
    res := 0;
    if (n0 >= 0) 
        {n,m := n0, m0;} 
    else 
        {n,m := -n0, -m0;}
    while (0 < n)
        invariant 0 <= n 
        invariant n0 >= 0 ==> (n0 - n) * m == res;
        invariant n0 < 0 ==> (-n0 - n) * m == res;
        decreases n
  { 
        res := res + m; 
        n := n - 1; 
    }
}
