method ProductAlt(a: array<int>) returns (p: int)
  requires a != null;
  ensures p == product(a);
{
  var i := a.Length-1;
  p := 1;
  while (i >= 0)
		    invariant -1 <= i <= a.Length;
			invariant p == productAuxAlt(a,i+1,a.Length-1);
  {
		p := p * a[i];
		i := i - 1;
  }
  Lemma(a,0,a.Length-1);
}

