predicate isSorted(a: array<int>)
  reads a
{
    forall i, j :: 0 <= i < j < a.Length ==> a[i] <= a[j]
}
method binarySearch(a: array<int>, x: int) returns (index: int)
    requires isSorted(a)
    ensures -1 <= index < a.Length
    ensures 0 <= index < a.Length ==> a[index] == x
    ensures index == -1 ==> (forall i:: 0 <= i < a.Length ==> a[i] != x)
{   
    var low, high := 0, a.Length;
    while low < high 
        decreases high - low
        invariant 0 <= low <= high <= a.Length
        invariant isSorted(a)
        invariant forall i:: 0 <= i < low ==> a[i] < x 
        invariant forall i:: high <= i < a.Length ==> a[i] > x 
    {
        var mid := low + (high - low) / 2;
        if 
        {
            case a[mid]  < x => low := mid + 1;
            case a[mid]  > x => high := mid;
            case a[mid] == x => return mid;
        }
    }
    return -1;
}
predicate sorted(a: array<int>, from: nat, to: nat)
  requires 0 <= from <= to <= a.Length
  reads a
{
    forall i, j :: from <= i < j < to ==> a[i] <= a[j] 
}
method insertionSort(a: array<int>)
    modifies a
    ensures isSorted(a)
    ensures multiset(a[..]) == multiset(old(a[..]))
{
    var i := 0;
    while i < a.Length 
        decreases a.Length - i
        invariant 0 <= i <= a.Length
        invariant forall m, n :: 0 <= m < n < i ==> a[m] <= a[n]
        invariant multiset(a[..]) == multiset(old(a[..]))
    {
        var j := i;
        while j > 0 && a[j-1] > a[j]
            decreases j
            invariant 0 <= j <= i
            invariant forall m, n :: 0 <= m < n < i+1 && n !=j ==> a[m] <= a[n]
            invariant multiset(a[..]) == multiset(old(a[..]))
        {
            a[j-1], a[j] := a[j], a[j-1];
            j := j - 1;
        }
        i := i + 1;
    }
}

method Search1000( a: array<int>, x: int ) returns ( k: int )
    requires a.Length >= 1000;
    requires forall p,q | 0 <= p < q < 1000 :: a[p] <= a[q];
    ensures 0 <= k <= 1000;
    ensures forall r | 0 <= r < k :: a[r] < x;
    ensures forall r | k <= r < 1000 :: a[r] >= x;
