method Multiply(N: int, M: nat) returns (R: int)
  ensures R == M*N;
  requires N>=0 && M>=0;
{
  var a := N;
  var b := M;
  var x := 0;
  while (b > 0)
  invariant b >= 0;
  invariant M*N == x + a*b;
  decreases b;
  {
    while (b % 10 != 0)
    invariant b >= 0;
    invariant M*N == x + a*b;
    decreases b
    {
      x := x + a;
      b := b - 1;
    }
    a := 10 * a;
    b := b / 10;
  }
  R := x;
}

