  min := arr[0];
  while (i < arr.Length)
    decreases arr.Length - i;
    invariant i < arr.Length + 1 ;
    invariant i > 0 ==> min <= arr[i-1] ;
    invariant arr != null && arr.Length > 0;
    invariant forall k :: 0 <= k < i ==> min <= arr[k];
  {
    if (arr[i] < min)
       {min := arr[i];}
       i := i +1;
  }
}

