method isPrefix(pre: string, str: string) returns (res: bool)
  requires |pre| <= |str|;  
{
  if (|pre| == 0) {
    return true;
  }
  var i: nat := 0;
  var a: char;
  var b: char;
  while (i < |pre|)
    decreases |pre| - i;
    invariant 0 <= i <= |pre|
  {
    a := pre[i];
    b := str[i];
    if (a != b) {
      return false;
    }
    i := i + 1;
  }
  return true;
}
method isSubstring(sub: string, str: string) returns (res: bool)
  requires |sub| <= |str|; 
{
  if (|sub| == 0){
    return true;
  }
  var i: nat := 0;
  while (i <= (|str| - |sub|))
    decreases |str| - |sub| - i;
    invariant 0 <= i;
    invariant i <= (|str| - |sub|) + 1;
  {
    var slice: string := str[i..];
    var subIsPrefixOfSlice: bool := isPrefix(sub, slice);
    if (subIsPrefixOfSlice) {
      return true;
    }
    i := i + 1;
  }
  return false;
}
method haveCommonKSubstring(k: nat, str1: string, str2: string) returns (found: bool)
  ensures (k > |str1| || k > |str2|) ==> found == false;
  ensures found == true ==> (k <= |str1| && k <= |str2|);
{
  var candidate: string;
  var i: nat := 0;
  var isASubstring: bool;
  if (k == 0) {
    return true;
  }
  if (k > |str1| || k > |str2|) {
    return false;
  }
  while (i <= |str1| - k)
    decreases |str1| - k - i;
    invariant i <= i+k-1;
  {
    candidate := str1[i..i+k-1];
    isASubstring := isSubstring(candidate, str2);
    if (isASubstring) {
      return true;
    }
    i := i + 1;
  }
  return false;
}
method maxKCommonSubString(str1: string, str2: string) returns (k: nat)
  ensures k <= |str1| && k <= |str2|;
  ensures (|str1| == 0 || |str2| == 0 ) ==> k == 0;
{
  var shorter: string;
  var longer: string;
  var thereIsACommonSubstring: bool := false;
  k := 0;
  if (|str1| <= |str2|) {
    shorter := str1;
    longer := str2;
  } else {
    shorter := str2;
    longer := str1;
  }
  while (k <= |shorter|)
    decreases |shorter| - k;
  {
    thereIsACommonSubstring := haveCommonKSubstring(k, shorter, longer);
    if (!thereIsACommonSubstring) {
        break;
    }
    k := k + 1;
  }
  return 0;
}

method CountLessThan(numbers: set<int>, threshold: int) returns (count: int)
{
  count := 0;
  var shrink := numbers;
  var grow := {};
  while |shrink | > 0
    decreases shrink
    invariant shrink + grow == numbers
    invariant grow !! shrink
    invariant count == |set i | i in grow && i < threshold|
  {
    var i: int :| i in shrink;
    shrink := shrink - {i};
    var grow' := grow+{i};
    assert (set i | i in grow' && i < threshold) ==
           (set i | i in grow && i < threshold )+ if i < threshold then {i} else {};
    grow := grow + {i};
    if i < threshold {
      count := count + 1;
    }
  }
}

