{return x+1;}
method add_by_one (x:int, y:int) returns (r:int)
{
  assume (y >= 0);
  var i:int := 0;
  r := x;
  assert (i <= y);
  assert (r == x + i);
  r := *;
  i := *;
  assume (i <= y);
  assume (r == x + i);
  if (i < y)
  {
    assume (i < -2);
    var t := y - i;
    r := r + 1;
    i := i + 1;
    assert (i <= y);
    assert (r == x + i);
    assert (y-i >= 0);
    assert (y-i < t);
    assume (false);
  }
  assert (r == x + y);
  return r;
}

datatype Tree<Data> = Nil | Leaf(key: int, value: Data)
		| Tree(key: int, value: Data, left: Tree, right: Tree)
datatype Option<T> = None | Some(T) 
function LookUp<Data>(key: int, t: Tree): Option<Data> 
{
	match t
	case Nil => None
	case Leaf(k, v) => if k == key then Some(v) else None
	case Tree(k, v, l, r) => if k == key then Some(v)
		else if key < k then LookUp(key, l) else LookUp(key, r)         
}
function Add<Data>(key: int, value: Data, t: Tree): Tree
{
	match t
    case Nil => Leaf(key, value)
	case Leaf(k, v) => if k == key then Leaf(k, value) 
                       else if key < k then Tree(k,v,Leaf(key, value), Nil) 
                           else Tree(k, v, Nil, Leaf(key, value)) 
    case Tree(k, v, l, r) => if k == key then Tree(k, value, l, r)
                             else if key < k then Tree(k, v, Add(key, value, l), r)
                                  else Tree(k, v, l, Add(key, value, r))
}
predicate Ordered<Data>(t: Tree<Data>) {
    match t
    case Nil => true
    case Leaf(_, _) => true
    case Tree(k, _, l, r) => (l != Nil ==> k > l.key && Ordered(l)) && 
                             (r != Nil ==> k < r.key && Ordered(r))
}
lemma AddLemma<Data>(k: int, v: Data, t:Tree<Data>)
    requires Ordered(t)
    ensures Ordered(Add(k,v,t))
{}

function max(x:nat, y:nat) : nat
{
  if (x < y) then y else x
}
method slow_max(a: nat, b: nat) returns (z: nat)
  ensures z == max(a, b);
{
  z := 0;
  var x := a;
  var y := b;
  while (z < x && z < y)
    invariant 0 <= x <= a;
    invariant 0 <= y <= b;
    invariant z == a-x || z == b-y;
    invariant a-x == b-y;
    decreases x - z;
    decreases y - z;
  {
    z := z + 1;
    x := x - 1;
    y := y - 1;
  }
 if (x <= y) { return b; }
 else { return a;}
}

function F(n: nat): nat { if n <= 2 then n else F(n-1) + F(n-3)}
method calcF(n: nat) returns (res: nat)  
 ensures res == F(n) 
{
  var a, b, c := 0, 1, 2;
  var i := 0;
  assert a == F(0) && b == F(1) && c == F(2); 
  while i < n 
  decreases n - i
  invariant 0 <= i <= n
  invariant a == F(i) && b == F(i + 1) && c == F(i + 2)
  {
    a, b, c := b, c, a + c;        
    i := i + 1;
	assert 0 <= i <= n && a == F(i) && b == F(i + 1) && c == F(i + 2);
  }
  res := a;
  assert (a == F(i) && b == F(i + 1) && c == F(i + 2)) ==> res == F(n);
}

