    requires x >= 1 
    ensures exp(x,e) >= 1
{}
lemma prodMon_Lemma(z:int, a:int, b:int)
    requires z >= 1 && a >= b >= 1
    ensures  z*a >= z*b
{}
lemma expMon_Lemma(x:int, n:nat)
	requires x >= 1 && n >= 1
	ensures exp(x+1,n) >= exp(x,n) + 1 
{
    if n != 1 { 
        calc {
            exp(x+1,n);
            ==
            (x+1)*exp(x+1,n-1);
            == 
            x*exp(x+1,n-1) + exp(x+1,n-1);
            >= {
                expGET1_Lemma(x+1,n-1);
            }
            x*exp(x+1,n-1);
            >= {
                expMon_Lemma(x,n-1);
                expGET1_Lemma(x+1,n-1);
                expGET1_Lemma(x,n-1);
                prodMon_Lemma(x, exp(x+1,n-1), exp(x,n-1) + 1);
            }
            x*(exp(x,n-1) + 1); 
            ==
            x*exp(x,n-1) + x;
            >=
            exp(x,n)+1;
        }
    }
}

