method join(a:array<int>,b:array<int>) returns (c:array<int>)
ensures a[..] + b[..] == c[..]
ensures multiset(a[..] + b[..]) == multiset(c[..])
ensures multiset(a[..]) + multiset(b[..]) == multiset(c[..])
ensures a.Length+b.Length == c.Length
ensures forall i :: 0<=i<a.Length ==> c[i] == a[i]
ensures forall i_2,j_2::
    a.Length <= i_2 < c.Length &&
    0<=j_2< b.Length && i_2 - j_2 == a.Length  ==> c[i_2] == b[j_2]
{
    c := new int[a.Length+b.Length];
    var i:= 0;
    while(i < a.Length)
    invariant 0 <= i <=a.Length
    invariant c[..i] == a[..i]
    invariant multiset(c[..i]) == multiset(a[..i])
    invariant forall k :: 0<=k<i<a.Length ==> c[k] == a[k]
    {
        c[i] := a[i];
        i := i +1;
    }
    i:= a.Length;
    var j := 0;
    while(i < c.Length && j<b.Length) 
    invariant 0<=j<=b.Length 
    invariant 0 <= a.Length <= i <= c.Length 
    invariant c[..a.Length] == a[..a.Length]
    invariant c[a.Length..i] == b[..j] 
    invariant c[..a.Length] + c[a.Length..i] == a[..a.Length] + b[..j] 
    invariant multiset(c[a.Length..i]) == multiset(b[..j]) 
    invariant multiset( c[..a.Length] + c[a.Length..i]) == multiset(a[..a.Length] + b[..j]) 
    invariant forall k :: 0<=k<a.Length ==> c[k] == a[k]  
    invariant forall i_2,j_2::
    a.Length <= i_2 < i &&
    0<=j_2< j && i_2 - j_2 == a.Length  ==> c[i_2] == b[j_2] 
    invariant forall k_2,i_2,j_2::
    0<=k_2<a.Length &&
    a.Length <= i_2 < i &&
    0<=j_2< j && i_2 - j_2 == a.Length  ==> c[i_2] == b[j_2] && c[k_2] == a[k_2] 
    {
        c[i] := b[j];
        i := i +1;
        j := j +1;
    }
    assert a[..] + b[..] == c[..];
    assert multiset(a[..]) + multiset(b[..]) == multiset(c[..]); 
}
method Check(){
    var a := new int[][1,2,3];
    var b := new int[][4,5];
    var c := new int[][1,2,3,4,5];
    var d:= join(a,b);
    assert d[..] == a[..] + b[..]; 
    assert multiset(d[..]) == multiset(a[..] + b[..]);
    assert multiset(d[..]) == multiset(a[..]) + multiset(b[..]);
    assert d[..] == c[..]; 
    assert d[..] == c[..]; 
}

datatype Exp = Const(int) | Var(string) | Plus(Exp, Exp) |  Mult(Exp, Exp)
function eval(e:Exp, store:map<string, int>):int
{
	match(e)
		case Const(n) => n
		case Var(s) => if(s in store) then store[s] else -1
		case Plus(e1, e2) => eval(e1, store) + eval(e2, store)
		case Mult(e1, e2) => eval(e1, store) * eval(e2, store)
}
function optimize(e:Exp):Exp
{
	match e
	case Mult(Const(0), e) => Const(0)
	case Mult(e, Const(0)) => Const(0)
	case Mult(Const(1), e) => e
	case Mult(e, Const(1)) => e
	case Mult(Const(n1), Const(n2)) => Const(n1*n2)
	case Plus(Const(0), e) => e
	case Plus(e, Const(0)) => e
	case Plus(Const(n1), Const(n2)) => Const(n1+ n2)
	case e => e
} 
method optimizeCorrect(e:Exp, s:map<string, int>)
ensures eval(e,s) == eval(optimize(e), s)
{
}
method optimizeFeatures()
{
	assert( optimize(Mult(Var("x"), Const(0))) == Const(0) );
	assert( optimize(Mult(Var("x"), Const(1))) == Var("x") );
	assert( optimize(Mult(Const(0), Var("x"))) == Const(0) );
	assert( optimize(Mult(Const(1), Var("x"))) == Var("x") );
	assert( optimize(Plus(Const(0), Var("x"))) == Var("x") );
	assert( optimize(Plus(Var("x"), Const(0))) == Var("x") );
	assert( optimize(Plus(Const(3),Const(4))) == Const(7) );
	assert( optimize(Mult(Const(3),Const(4))) == Const(12) );
	assert( optimize(Plus(Plus(Var("x"), Var("y")), Const(0))) == Plus(Var("x"), Var("y")) );
}

