method is_anagram(s: string, t: string) returns (result: bool)
    requires |s| == |t|
    ensures (multiset(s) == multiset(t)) == result
{
    result := is_equal(multiset(s), multiset(t));
}
method is_equal(s: multiset<char>, t: multiset<char>) returns (result: bool)
    ensures (s == t) <==> result
{
    var s_removed := multiset{};
    var s_remaining := s;
    while (|s_remaining| > 0)
        invariant s_remaining == s - s_removed
        invariant forall removed :: removed in s_removed ==> (removed in s &&
                                                              removed in t &&
                                                              s[removed] == t[removed])
    {
        var remaining :| remaining in s_remaining;
        if !(remaining in s &&
             remaining in t &&
             s[remaining] == t[remaining]) {
           return false; 
        }
        var temp := multiset{};
        s_removed := s_removed + temp[remaining := s[remaining]];
        s_remaining := s_remaining - temp[remaining := s[remaining]];
    }
    var t_removed := multiset{};
    var t_remaining := t;
    while (|t_remaining| > 0)
        invariant t_remaining == t - t_removed
        invariant forall removed :: removed in t_removed ==> (removed in s &&
                                                              removed in t &&
                                                              s[removed] == t[removed])
    {
        var remaining :| remaining in t_remaining;
        if !(remaining in s &&
             remaining in t &&
             s[remaining] == t[remaining]) {
           return false; 
        }
        var temp := multiset{};
        t_removed := t_removed + temp[remaining := t[remaining]];
        t_remaining := t_remaining - temp[remaining := t[remaining]];
    }
    return true;
}

predicate sorted_between(A:array<int>, from:int, to:int)
    reads A
{
    forall i, j :: 0 <= i <= j < A.Length && from <= i <= j <= to ==> A[i] <= A[j]
}
predicate sorted(A:array<int>)
    reads A
{
    sorted_between(A, 0, A.Length-1)
}
method BubbleSort(A:array<int>)
    modifies A
    ensures sorted(A)
    ensures multiset(A[..]) == multiset(old(A[..]))
{
    var N := A.Length;
    var i := N-1;
    while 0 < i
        invariant multiset(A[..]) == multiset(old(A[..]))
        invariant sorted_between(A, i, N-1)
        invariant forall n, m :: 0 <= n <= i < m < N ==> A[n] <= A[m]
        decreases i
    {
        print A[..], "\n";
        var j := 0;
        while j < i
            invariant 0 < i < N
            invariant 0 <= j <= i
            invariant multiset(A[..]) == multiset(old(A[..]))
            invariant sorted_between(A, i, N-1)
            invariant forall n, m :: 0 <= n <= i < m < N ==> A[n] <= A[m]
            invariant forall n :: 0 <= n <= j ==> A[n] <= A[j]
            decreases i - j
        {
            if A[j] > A[j+1]
            {
                A[j], A[j+1] := A[j+1], A[j];
                print A[..], "\n";
            }
            j := j+1;
        } 
        i := i-1;
        print "\n";
    }
}
method Main() {
    var A := new int[10];
    A[0], A[1], A[2], A[3], A[4], A[5], A[6], A[7], A[8], A[9] := 2, 4, 6, 15, 3, 19, 17, 16, 18, 1;
    BubbleSort(A);
    print A[..];
}

module ProdCons {
    type Process(==) 
    type T
    class ProdCons { 
        const P: set<Process>
        var maxBufferSize : nat 
        var buffer : seq<T> 
        predicate valid() 
            reads this
        {
            maxBufferSize > 0 && P != {} &&
            0 <= |buffer| <= maxBufferSize 
        }
        constructor (processes: set<Process>, m: nat ) 
            requires processes != {}        
            requires m >= 1                 
            ensures valid()                 
        { 
            P := processes;
            buffer := [];
            maxBufferSize := m;
        }
        predicate putEnabled(p : Process) 
            reads this
        {
            |buffer| < maxBufferSize
        }
        method put(p: Process, t : T) 
            requires valid()                
            requires putEnabled(p)          
            modifies this 
        { 
            buffer := buffer + [t] ;
        }
        predicate getEnabled(p : Process) 
            reads this
        {
            |buffer| >= 1
        }
        method get(p: Process) 
            requires getEnabled(p)
            requires valid()                
            ensures |buffer| == |old(buffer)| - 1   
            modifies this 
        { 
           buffer := buffer[1..];
        }
        lemma noDeadlock() 
            requires valid() 
            ensures exists p :: p in P && (getEnabled(p) || putEnabled(p))
        { 
          var p: Process :| p in P ;
            if ( |buffer| > 0 ) {
                assert (getEnabled(p));
            }
            else {
                assert(|buffer| == 0);
                assert (|buffer| < maxBufferSize); 
                assert(putEnabled(p));
            }
        }
    }
}

