datatype Heap = H(a: array<int>, size: int, capacity: int)
predicate valid_heap(h: Heap)
	reads h.a
{
	h.a != null && h.a.Length == h.capacity + 1 && 0 <= h.size <= h.capacity &&
	    forall i :: 1 < i <= h.size ==> h.a[i] <= h.a[i / 2]
}
method init_heap(cap: int) returns (h: Heap)
	requires 0 <= cap
	ensures valid_heap(h)
	ensures h.size == 0 && h.capacity == cap
	ensures fresh(h.a)
{
	var a := new int[cap + 1];
	var size := 0;
	var capacity := cap;
	h := H(a, size, capacity);
}
method insert(x: int, h: Heap) returns (h': Heap)
	requires valid_heap(h)
	requires h.size < h.capacity
	ensures valid_heap(h')
	ensures h'.size == h.size + 1 && h'.capacity == h.capacity
	ensures fresh(h'.a)
{
	var size := h.size + 1;
	var cap := h.capacity;
	var a := new int[cap + 1];
	var i := 1;
	while (i <= h.size)
		decreases h.size - i
		invariant forall j :: 1 <= j < i ==> j <= h.size && a[j] == h.a[j]
	{
		a[i] := h.a[i];
		i := i + 1;
	}
	assert forall i :: 1 <= i <= h.size ==> a[i] == h.a[i];
	a[size] := a[size / 2];
	var current_place := size;
	while(current_place > 1 && x > a[current_place / 2])
		invariant 1 <= current_place <= h.size + 1 
		invariant forall i :: 1 < i <= h.size + 1 ==> a[i] <= a[i / 2]
		invariant forall i :: 1 < i <= h.size + 1 && i/2 == current_place ==> a[i] <= x
		decreases current_place
	{
		a[current_place] := a[current_place / 2];
		current_place := current_place / 2;
	}
	a[current_place]:= x;
	h' := H(a, size, cap);
}
lemma max_heap(h: Heap)
	requires valid_heap(h)
	requires h.size >= 1
	ensures forall i :: 1 < i <= h.size ==> h.a[i] <= h.a[1]
{
	assert forall i {:induction i} :: 1 < i <= h.size ==> ancestor (i, 1);
	assert forall i, j {:induction i, j} :: 1 <= i < j <= h.size && ancestor(j, i) ==> h.a[j] <= h.a[i];
}
predicate ancestor(a: int, b: int)
	decreases a
	requires 0 < a && 0 < b
{
	b < a && (b == a / 2 || ancestor(a/2, b))
}
method extract(h: Heap) returns (x: int, h': Heap)
	requires valid_heap(h)
	requires 0 < h.size
	ensures valid_heap(h')
	ensures h'.size == h.size - 1
	ensures forall i :: 1 <= i <= h'.size ==> h'.a[i] <= x
	ensures x == h.a[1]
	ensures fresh(h'.a)
{
	max_heap(h);
	x := h.a[1];
	var a := new int[h.capacity + 1];
	var i := 1;
	while (i <= h.size)
		decreases h.size - i
		invariant forall j :: 1 <= j < i ==> j <= h.size && a[j] == h.a[j]
	{
		a[i] := h.a[i];
		i := i + 1;
	}
	assert forall i :: 1 <= i <= h.size ==> a[i] == h.a[i];
	if(h.size == 1)
	{
		h' := H(a, h.size - 1, h.capacity);
		return;
	}
	var pivot := a[h.size];
	var place := 1;
  	while((place * 2 < h.size && a[place * 2] >= pivot) || (place * 2 + 1 < h.size && a[place * 2 + 1] >= pivot))
	invariant 1 <= place < h.size 
	invariant forall i :: 1 < i < h.size ==> a[i] <= a[i / 2] 
	invariant forall i :: 1 <= i <= h.size ==> a[i] <= x
	invariant a[place] >= pivot
	decreases h.size - place * 2
	{
    	if (place * 2 < h.size && a[place * 2] >= pivot && (place * 2 + 1 >= h.size || a[place * 2] >= a[place * 2 + 1]))
		{
			assert a[place*2] <= a[(place*2) / 2]; 
      		a[place] := a[place * 2];
      		place := place * 2;
		}
    	else
		{
      		if (place * 2 + 1 < h.size && a[place * 2 + 1] >= pivot && a[place * 2 + 1] > a[place * 2])
			{
				assert a[place*2 + 1] <= a[(place*2 + 1) / 2]; 
        		a[place] := a[place * 2 + 1];
        		place := place * 2 + 1;
			}
			else
			{
				a[place] := pivot;
				place := h.size - 1;
			}
		}
	}
	assert forall i :: 1 < i < h.size && place == i / 2 ==> pivot >= a[i];
	a[place] := pivot;
    h' := H(a, h.size - 1, h.capacity);
}
class Heapsort
{
	var h: Heap;
	method sort(a: array<int>)
		requires 0 <= a.Length
		ensures forall i :: 0 <= i < a.Length - 1 ==> a[i] >= a[i + 1]
		modifies this, h.a, a
	{
		this.h := init_heap(a.Length);
		assert this.h.size == 0;
		if(a.Length == 0) {
			return;
		}
		while (this.h.size < a.Length)
		invariant valid_heap(this.h)
		invariant a.Length == this.h.capacity
		decreases a.Length - this.h.size
		{
			assert(this.h.size < this.h.capacity);
			var k := a[this.h.size];
			this.h := insert(k, this.h);
		}
		assert this.h.size == a.Length;
		while(this.h.size >= 1)
		invariant valid_heap(this.h)
		invariant forall i :: 0 <= i < a.Length - this.h.size - 1 ==> a[i] >= a[i + 1]
		invariant forall i :: 0 <= i <= a.Length - this.h.size - 1 && this.h.size > 0 ==> a[i] >= this.h.a[1]
		decreases this.h.size
		{
			var j;
			j, this.h := extract(this.h);
			assert this.h.size < a.Length;
			a[a.Length - this.h.size - 1] := j;
			if(this.h.size > 0)
			{
				assert a[a.Length - this.h.size - 1] >= this.h.a[1];
			}
		}
	}
}

method Abs(x : int) returns (y : int)
  ensures 0 <= y;
  ensures 0 <= x ==> y == x;
  ensures x < 0 ==> y == -x;
{
  if (x < 0)
   {y := -x;}
  else
   {y := x;}
}

method filter(a:array<int>, key:int) returns (b:seq<int>)
	ensures |b| <= a.Length;
	ensures forall k :: 0 <= k < |b| ==> b[k] == key;  
	ensures forall j :: 0 <= j < |b| ==> b[j] in a[0..]; 
	ensures forall k :: 0 <= k < a.Length && a[k] == key ==> a[k] in b;
{
	var i:int := 0;
	b := [];
	while (i < a.Length)
		invariant |b| <= i <= a.Length;
		invariant forall k :: 0 <= k < |b|==> b[k] == key;
		invariant forall j:: 0 <= j < |b| ==> b[j] in a[0..i];
		invariant forall k :: 0 <= k < i && a[k]==key ==> a[k] in b;
		decreases a.Length-i;
	{
		if (a[i] == key) 
		{ 
			b := b + [a[i]];
		}
		i := i + 1;
	}
}

module {:options "--function-syntax:4"} Dafny.Wrappers {
  datatype Option<+T> = None | Some(value: T) {
    predicate IsFailure() {
      None?
    }
    function PropagateFailure<U>(): Option<U>
      requires None?
    {
      None
    }
    function Extract(): T
      requires Some?
    {
      value
    }
    function GetOr(default: T): T {
      match this
      case Some(v) => v
      case None() => default
    }
    function ToResult<E>(error: E): Result<T, E> {
      match this
      case Some(v) => Success(v)
      case None() => Failure(error)
    }
    function ToOutcome<E>(error: E): Outcome<E> {
      match this
      case Some(v) => Pass
      case None() => Fail(error)
    }
    function Map<FC>(rewrap: Option<T> -> FC): FC
    {
      rewrap(this)
    }
  }
  datatype Result<+R, +E> = | Success(value: R) | Failure(error: E) {
    predicate IsFailure() {
      Failure?
    }
    function PropagateFailure<U>(): (r: Result<U, E>)
      requires Failure?
    {
      Failure(this.error)
    }
    function Extract(): R
      requires Success?
    {
      value
    }
    function GetOr(default: R): R
    {
      match this
      case Success(s) => s
      case Failure(e) => default
    }
    function ToOption(): Option<R>
    {
      match this
      case Success(s) => Some(s)
      case Failure(e) => None()
    }
    function ToOutcome(): Outcome<E>
    {
      match this
      case Success(s) => Pass
      case Failure(e) => Fail(e)
    }
    function Map<FC>(rewrap: Result<R,E> -> FC): FC
    {
      rewrap(this)
    }
    function MapFailure<NewE>(reWrap: E -> NewE): Result<R, NewE>
    {
      match this
      case Success(s) => Success(s)
      case Failure(e) => Failure(reWrap(e))
    }
  }
  datatype Outcome<+E> = Pass | Fail(error: E) {
    predicate IsFailure() {
      Fail?
    }
    function PropagateFailure(): Outcome<E>
      requires Fail?
    {
      this
    }
    function ToOption<R>(r: R): Option<R>
    {
      match this
      case Pass => Some(r)
      case Fail(e) => None()
    }
    function ToResult<R>(r: R): Result<R,E>
    {
      match this
      case Pass => Success(r)
      case Fail(e) => Failure(e)
    }
    function Map<FC>(rewrap: Outcome<E> -> FC): FC
    {
      rewrap(this)
    }
    function MapFailure<T,NewE>(rewrap: E-> NewE, default: T): Result<T, NewE>
    {
      match this
      case Pass => Success(default)
      case Fail(e) => Failure(rewrap(e))
    }
    static function Need(condition: bool, error: E): (result: Outcome<E>)
    {
      if condition then Pass else Fail(error)
    }
  }
  datatype OutcomeResult<+E> = Pass' | Fail'(error: E) {
    predicate IsFailure() {
      Fail'?
    }
    function PropagateFailure<U>(): Result<U,E>
      requires IsFailure()
    {
      Failure(this.error)
    }
  }
  function Need<E>(condition: bool, error: E): (result: OutcomeResult<E>)
  {
    if condition then Pass' else Fail'(error)
  }
  method Expect(condition: bool, message: string)
    requires condition
  {
    expect condition, message;
  }
}

function fib(n: nat): nat
{
    if n == 0 then
        0
    else if n == 1 then
        1
    else
        fib(n - 1) + fib(n - 2)
}
method ComputeFib(n: nat) returns (b: nat)
    ensures b == fib(n)
{
    if n == 0 {
        return 0;
    }
    var i: int := 1;
    var c := 1;
    b := 1;
    while i < n
        invariant 0 < i <= n
        invariant b == fib(i)
        invariant c == fib(i + 1)
    {
        b, c := c, b + c;
        i := i + 1;
    }
}

