  requires a != null;
  requires l1 <= u1 <= l2 <= u2 < a.Length;
  reads(a);
{
  forall i, j :: l1 <= i <= u1 < l2 <= j <= u2 ==> a[i] <= a[j]
}

method MinOfMultiset( m: multiset<int> ) returns( min: int )
    requires m != multiset{};
    ensures min in m;
    ensures forall z | z in m :: min <= z;
{
    min :| min in m;
    var done := multiset{min};
    var m' := m-done;
    while m' != multiset{}
        decreases m';
        invariant m == done+m';
        invariant min in done;
        invariant forall z | z in done :: min <= z;
    {
        var z :| z in m';
        done := done+multiset{z};
        m' := m'-multiset{z};
        if z < min { min := z; }
    }
}
