   assert sum == 20;
   sum := IncDec(5,-15);
   assert sum == -10;
   sum := IncDec(5,0);
   assert sum == 5;
   sum := IncDec(-5,15);
   assert sum == 10;
   sum := IncDec(-5,-15);
   assert sum == -20;
   sum := IncDec(-5,0);
   assert sum == -5;
}

