method mc91(n: nat) returns (b: nat)
  requires n <= 101;
  ensures b == 91; 
{
  var m, c;
  c := 1;
  m := n;
  while (c > 0)
    invariant m <= 111;
    invariant m > 100 && c == 1 ==> m == 101;
    invariant c == 0 ==> m == 91;
    decreases 21 * c + (400 - 2 * m);
  {
    if (m > 100) {
      m := m - 10;
      c := c - 1;
    }
    else {
      m := m + 11;
      c := c + 1;
    }
  }
  return m;
}

method Add(x: int, y: int) returns (r: int)
  ensures r == x+y;
{
  r := x;
  if (y < 0) {
    var n := y;
    while (n != 0)
      invariant r == x+y-n && 0 <= -n;
    {
      r := r - 1;
      n := n + 1;
    }
  } else {
    var n := y;
    while (n != 0)
      invariant r == x+y-n && 0 <= n;
    {
      r := r + 1;
      n := n - 1;
    }
  }
}
method Mul(x: int, y: int) returns (r: int)
  ensures r == x*y;
  decreases x < 0, x;
{
  if (x == 0) {
    r := 0;
  } else if (x < 0) {
    r := Mul(-x, y);
    r := -r;
  } else {
    r := Mul(x-1, y);
    r := Add(r, y);
  }
}
