method LinearSearch0<T>(a: array<T>, P:T -> bool) returns (n: int)
    ensures 0 <= n <= a.Length
    ensures n == a.Length || P(a[n])
{
    n := 0;
    while n != a.Length
        invariant 0 <= n <= a.Length
        decreases if n <= a.Length then a.Length - n else n - a.Length
    {
        if P(a[n]) {
            return;
        }
        n := n + 1;
    }
