method bubble(a: array<int>)
  requires a != null;
  modifies a;
  ensures sorted(a);
{
  var i, n, s;
  n := a.Length;
  s := a.Length > 0;
  while (s)
    invariant sorted_slice(a, n, a.Length - 1);
    invariant forall j, k :: 0 <= j < n && n <= k < a.Length ==> a[j] <= a[k];
    invariant !s ==> sorted_slice(a, 0, n);
    invariant n > 0 || !s;
    decreases n;
  {
    s := false;
    i := 1;
    while (i <= n - 1)
      invariant n <= 1 ==> !s;
      invariant 1 <= i && i <= n;
      invariant sorted_slice(a, n, a.Length - 1);
      invariant forall j, k :: 0 <= j < n && n <= k < a.Length ==> a[j] <= a[k];
      invariant forall j :: 0 <= j <= i - 1 ==> a[j] <= a[i - 1]; 
      invariant !s ==> sorted_slice(a, 0, i - 1);
    {
      if (a[i - 1] > a[i]) {
         a[i - 1], a[i] := a[i], a[i - 1];
	       s := true;
      }
      i := i + 1;
    }
    n := n - 1;
  }
}

ghost predicate Extends<K, V>(m1: map<K, V>, m2: map<K, V>) {
    forall k :: k in m2 ==> k in m1 && m1[k] == m2[k]
}
ghost predicate UndefOn<K(!new), V>(m: map<K, V>, p: K -> bool) {
    forall k :: p(k) ==> k !in m
}
ghost predicate Disjoint<K(!new)>(p1: K -> bool, p2: K -> bool) {
    forall k :: !(p1(k) && p2(k))
}
ghost predicate Subset<K(!new)>(p1: K -> bool, p2: K -> bool) {
    forall k :: p1(k) ==> p2(k)
}
ghost predicate OnlyDiffer<K(!new), V>(m1: map<K, V>, p: K -> bool, m2: map<K, V>) {
    forall k :: p(k) || (k !in m1 && k !in m2) || (k in m1 && k in m2 && m1[k] == m2[k])
}
function Diff<K>(p1: K -> bool, p2: K -> bool): K -> bool {
    k => p1(k) && !p2(k)
}
lemma flattenStmt_correct_aux_lemma6<K(!new), V>(
    initialH: map<K, V>, initialL: map<K, V>, 
    fvngs: K -> bool, emv: K -> bool, av: V, vv : V,
    v: K, v0 : K, 
    prefinalL: map<K, V>, finalL: map<K, V>, 
    fvn: K -> bool, fvngs': K -> bool, mvs0: K -> bool, mvs: K -> bool
)
    requires Extends(initialL, initialH)
    requires UndefOn(initialH, fvngs)
    requires Disjoint(emv, fvngs)
    requires v in prefinalL && prefinalL[v] == av
    requires v0 in finalL && finalL[v0] == vv
    requires Subset(fvngs', fvn)
    requires Subset(fvn, fvngs)
    requires OnlyDiffer(prefinalL, mvs0, finalL)
    requires OnlyDiffer(initialL, mvs, prefinalL)
    requires mvs0(v0)
    requires mvs(v)
    requires Subset(mvs0, Diff(fvn, fvngs'))
    requires Subset(mvs, Diff(fvngs, fvn))
    ensures Extends(finalL, initialH)
{}

method pqst<b(==)>(ghij: array<b>, lmno: b) returns (rstu: bool)
{
  var uvwx := 0;
  rstu := false;
  var yzab := 0;
  while uvwx < ghij.Length
    invariant 0 <= uvwx <= ghij.Length
    invariant multiset(ghij[..uvwx])[lmno] == yzab
    invariant rstu <==> (yzab == 1)
  {
    if (ghij[uvwx] == lmno)
    { yzab := yzab + 1; }
    if (yzab == 1)
    { rstu := true; }
    else
    { rstu := false; }
    uvwx := uvwx + 1;
  }
  assert ghij[..ghij.Length] == ghij[..];
}

