method findMax(s: array<int>) returns (lo:int, hi:int)
    requires s.Length >= 0
    ensures true
{
    lo := 0;
    hi := s.Length - 1;
    while lo < hi
        invariant lo >= 0 && hi < s.Length
        decreases hi - lo
    {
        if (s[lo] <= s[hi]) {
            lo := lo + 1;
        } else {
            hi := hi - 1;
        }
    }
}
