module {:options "-functionSyntax:4"} Wrappers {
  datatype Option<+T> = None | Some(value: T) {
    function ToResult(): Result<T, string> {
      match this
      case Some(v) => Success(v)
      case None() => Failure("Option is None")
    }
    function ToResult'<R>(error: R): Result<T, R> {
      match this
      case Some(v) => Success(v)
      case None() => Failure(error)
    }
    function UnwrapOr(default: T): T {
      match this
      case Some(v) => v
      case None() => default
    }
    predicate IsFailure() {
      None?
    }
    function PropagateFailure<U>(): Option<U>
      requires None?
    {
      None
    }
    function Extract(): T
      requires Some?
    {
      value
    }
  }
  datatype Result<+T, +R> = | Success(value: T) | Failure(error: R) {
    function ToOption(): Option<T>
    {
      match this
      case Success(s) => Some(s)
      case Failure(e) => None()
    }
    function UnwrapOr(default: T): T
    {
      match this
      case Success(s) => s
      case Failure(e) => default
    }
    predicate IsFailure() {
      Failure?
    }
    function PropagateFailure<U>(): Result<U, R>
      requires Failure?
    {
      Failure(this.error)
    }
    function MapFailure<NewR>(reWrap: R -> NewR): Result<T, NewR>
    {
      match this
      case Success(s) => Success(s)
      case Failure(e) => Failure(reWrap(e))
    }
    function Extract(): T
      requires Success?
    {
      value
    }
  }
  datatype Outcome<E> = Pass | Fail(error: E)
  {
    predicate IsFailure() {
      Fail?
    }
    function PropagateFailure<U>(): Result<U, E>
      requires Fail?
    {
      Failure(this.error)
    }
  }
  function Need<E>(condition: bool, error: E): (result: Outcome<E>)
  {
    if condition then Pass else Fail(error)
  }
}

method appendArray(a: array<int>, b: array<int>) returns (c: array<int>)
ensures c.Length == a.Length + b.Length
ensures forall i :: 0 <= i < a.Length ==> a[i] == c[i]
ensures forall i :: 0 <= i < b.Length ==> b[i] == c[a.Length + i]
{
	c := new int[a.Length + b.Length];
	var i := 0;
	while i < a.Length
	invariant 0 <= i <= a.Length
	invariant forall j :: 0 <= j < i ==> c[j] == a[j]
	{
		c[i] := a[i];
		i := i + 1;
	}
	while i < b.Length + a.Length
	invariant a.Length <= i <= b.Length + a.Length
	invariant forall j :: 0 <= j < a.Length ==> a[j] == c[j]
	invariant forall j :: a.Length <= j < i ==> c[j] == b[j - a.Length]
	{
		c[i] := b[i - a.Length];
		i := i + 1;
	}
}

ghost function Sum(a: seq<int>, s: int, t: int): int
  requires 0 <= s <= t <= |a|
{
  if s == t then 0 else Sum(a, s, t-1) + a[t-1]
}
method MaxSegSum(a: seq<int>) returns (k: int, m: int)
  ensures 0 <= k <= m <= |a|
  ensures forall p,q :: 0 <= p <= q <= |a| ==> Sum(a, p, q) <= Sum(a, k, m)
{
  k, m := 0, 0;
  var s := 0;  
  var n := 0;
  var c, t := 0, 0;  
  while n < |a|
    invariant 0 <= c <= n <= |a| && t == Sum(a, c, n)
    invariant forall b :: 0 <= b <= n ==> Sum(a, b, n) <= Sum(a, c, n)
    invariant 0 <= k <= m <= n && s == Sum(a, k, m)
    invariant forall p,q :: 0 <= p <= q <= n ==> Sum(a, p, q) <= Sum(a, k, m)
  {
    t, n := t + a[n], n + 1;
    if t < 0 {
      c, t := n, 0;
    } else if s < t {
      k, m, s := c, n, t;
    }
  }
}

class Queue<T> {
