function fib(n : nat ) : nat
  decreases n
{
    if n < 2 then n else fib(n - 2) + fib(n - 1)
}
method computeFib (n : nat) returns (x : nat)
    ensures x == fib(n)
{
    var i := 0;
    x := 0;
    var y := 1;
    while  i < n 
        decreases n - i
        invariant 0 <= i <= n
        invariant x == fib(i)
        invariant y == fib(i+1)
    {
        x, y := y, x + y; 
        i := i + 1;
    }
}
