trait S {
  ghost var obs: set<O>
  ghost function upCast(o: object): object {o}
  ghost predicate i0() reads this, obs { forall o: O | o in obs :: o.s == this && upCast(o) != this }
  ghost predicate i() reads * { i0() && forall o: O | o in obs :: o.i() }
  twostate predicate i2() reads * { forall o: O | o in old(obs) :: o in obs && o.i2() }
  twostate predicate legal0() reads * { forall o: O | o in old(obs) :: unchanged(o) || (o.i2() && o.i()) }
  twostate predicate legal1() reads * { forall o: O | o in obs && o !in old(obs) :: o.i() }
  twostate predicate legal() reads * { old(i()) && i0() && old(obs) <= obs && legal0() && legal1() }
  twostate lemma lci() requires legal() ensures i() && i2() {
    forall o: O | o in old(obs) && o.admPre() ensures o.i2() && o.i() { o.adm(); }
  }
}
trait O {
  ghost const s: S
  ghost predicate i0() reads * { this in s.obs && s.i0() }
  ghost method join()
    requires s.i0() && s.upCast(this) != s
    modifies s 
    ensures i0() && s.obs == old(s.obs) + { this }
  {
    s.obs := s.obs + { this }; 
  }
  twostate predicate admPre() reads * { i0() && old(i0() && s.i()) && unchanged(this) && s.legal() }
  ghost predicate gi() reads * { i0() && s.i() }
  twostate predicate gi2() requires old(gi()) reads * { i0() && s.i2() }
  ghost predicate i() reads *
  twostate predicate i2() reads *
  twostate lemma adm() requires admPre() ensures i2() && i()
}
class Inner extends O {
  var data: int
  ghost predicate i() reads * {
    && i0()
  }
  twostate predicate i2() reads * {
    && old(data) <= data
  }
  twostate lemma adm() requires admPre() {}
  constructor (ghost s: S, initial_data: int)
    requires s.i()
    modifies s
    ensures s.i()
  {
    this.s := s;
    data := initial_data;
    new;
    join();
    s.lci();
  }
}
class Outer extends O {
  var inner: Inner
  ghost predicate i() reads * {
    && i0()
    && s == inner.s
    && inner.i()
  }
  twostate predicate i2() reads * {
    && inner == old(inner)
    && inner.i2()
  }
  twostate lemma adm() requires admPre() ensures i2() && i() {
  }
  constructor (ghost s: S, inner: Inner)
    requires s.i() && s == inner.s && inner.i()
    modifies s
    ensures s.i()
  {   
    this.s := s;
    this.inner := inner;
    new;
    join();
    s.lci();
  }
}

datatype Player = P1 | P2
{
  function Other(): Player {
    if this == P1 then P2 else P1
  }
}
datatype Variables = Variables(piles: seq<nat>, turn: Player)
ghost predicate Init(v: Variables) {
  && |v.piles| == 3
  && v.turn.P1? 
}
datatype Step =
  | TurnStep(take: nat, p: nat)
  | NoOpStep()
ghost predicate Turn(v: Variables, v': Variables, step: Step)
  requires step.TurnStep?
{
  var p := step.p;
  var take := step.take;
  && p < |v.piles|
  && take <= v.piles[p]
  && v' == v.(piles := v.piles[p := v.piles[p] - take]).(turn := v.turn.Other())
}
ghost predicate NextStep(v: Variables,  v': Variables, step: Step) {
  match step {
    case TurnStep(_, _) => Turn(v, v', step)
    case NoOpStep() => v' == v 
  }
}
lemma NextStepDeterministicGivenStep(v: Variables, v': Variables, v'': Variables, step: Step)
  requires NextStep(v, v', step)
  requires NextStep(v, v'', step)
  ensures v' == v''
{
}
