function F(n: nat): nat { if n <= 2 then n else F(n-1) + F(n-3)}
method calcF(n: nat) returns (res: nat)  
 ensures res == F(n) 
{
    assert 0 <= 0 <= n && 0 == F(0) && 1 == F(0+1) && 2 == F(0+2); 
    var a, b, c := 0, 1, 2;
    assert 0 <= 0 <= n && a == F(0) && b == F(0+1) && c == F(0+2); 
    var i := 0;
    assert 0 <= i <= n && a == F(i) && b == F(i+1) && c == F(i+2); 
    while i < n 
    decreases n - i 
    invariant 0 <= i <= n 
    invariant a == F(i) && b == F(i+1) && c == F(i+2)
    {
    ghost var V0 := n - i;
    assert 0 <= i <= n && a == F(i) && b == F(i+1) && c == F(i+2) && i < n && n - i == V0; 
    assert  0 <= i + 1 <= n && b == F(i + 1) && c == F(i + 1+1) && a+ c == F(i + 1 + 2) && 0 <= n - (i + 1)< V0;  
    assert (0 <= i <= n && a == F(i) && b == F(i+1) && c == F(i+2) && i < n && n - i == V0) ==> (0 <= i + 1 <= n && b == F(i + 1) && c == F(i + 1+1) && a+ c == F(i + 1 + 2) && 0 <= n - (i + 1)< V0);
    a, b, c := b, c, a + c;   
    assert  0 <= i + 1<= n && a == F(i + 1) && b == F(i + 1+1) && c == F(i + 1 + 2) && 0 <= n - (i + 1)< V0;   
    i := i + 1;
    assert  0 <= i <= n && a == F(i) && b == F(i+1) && c == F(i+2) && 0 <= n - i < V0; 
    }
    assert 0 <= i <= n && a == F(i) && b == F(i+1) && c == F(i+2) && i >= n; 
    assert a == F(n);
    assert (0 <= i <= n && a == F(i) && b == F(i+1) && c == F(i+2) && i >= n) ==> (a== F(n)); 
    res := a;
    assert res == F(n); 
}

