method FindMax (a:array<int>)  returns (max : int)
requires a != null;
requires a.Length > 1; 
ensures forall k:: 0 <= k < a.Length ==> a[k] <= max; 
{
  max := a[0];
  var i := 0;
  while (i < a.Length)
  invariant 0 <= i <= a.Length;
  decreases a.Length - i;
  invariant forall k:: 0 <= k < i ==> a[k] <= max;
  {
    if (a[i] > max) { max := a[i];} 
    i := i + 1;
  }
  return max;
}

predicate isSortedRanged(a: array<int>, x1: nat, x2: nat) 
    reads a
    requires 0 <= x1 <= x2 <= a.Length
{
    forall i, j :: x1 <= i < j < x2 ==> a[i] <= a[j]
}
predicate isSorted(a: array<int>)
    reads a
{
    isSortedRanged(a, 0, a.Length)
}
method insertionSort(a: array<int>)
    modifies a 
    ensures isSorted(a) 
    ensures multiset(a[..]) == multiset(old(a[..])) 
{
    var i := 0;
    while i < a.Length 
        decreases a.Length - i
        invariant multiset(a[..]) == multiset(old(a[..]))
        invariant 0 <= i <= a.Length
        invariant isSortedRanged(a, 0, i)
    {
        var j := i;
        while j > 0 && a[j-1] > a[j] 
            decreases j 
            invariant multiset(a[..]) == multiset(old(a[..]))
            invariant 0 <= j <= i
            invariant forall l, r :: (0 <= l < r <= i && j != r) ==> a[l] <= a[r]
        {
            a[j-1], a[j] := a[j], a[j-1];
            j := j - 1;
        }
        i := i + 1;
    }
}
