method swap<T>(a: array<T>, i: int, j: int)
  requires 0 <= i < j < a.Length
  modifies a
  ensures a[i] == old(a[j])
  ensures a[j] == old(a[i])
  ensures forall m :: 0 <= m < a.Length && m != i && 
                            m != j ==> a[m] == old(a[m])
  ensures multiset(a[..]) == old(multiset(a[..]))
{
  var t := a[i];
  a[i] := a[j];
  a[j] := t;
}
method two_way_sort(a: array<bool>)
  modifies a
  ensures a.Length > 1 ==> (forall k :: 0 <= k < a.Length - 1 ==> (a[k] && a[k+1]) || (!a[k] && a[k+1]) || (!a[k] && !a[k+1]))
  ensures multiset(a[..]) == old(multiset(a[..]))
{
  var i := 0;
  var j := a.Length - 1;
  while (i <= j)
   invariant 0 <= i <= j + 1 <= a.Length
   invariant forall m :: 0 <= m < i ==> !a[m]
   invariant forall n :: j < n < a.Length ==> a[n]
   invariant multiset(a[..]) == old(multiset(a[..]))
  {
    if (!a[i]) {
      i := i+1;
    } else if (a[j]) {
      j := j-1;
    } else {
      swap(a, i, j);
      i := i+1;
      j := j-1;
    }
  }
}

static function Power2(exp: int) : int
    requires exp >= 0;
    ensures Power2(exp) > 0;
{
    if (exp==0) then
        1
    else
        2*Power2(exp-1)
}
static lemma Lemma_Power2Adds(e1:nat, e2:nat)
	ensures Power2(e1 + e2) == Power2(e1) * Power2(e2);
{
	if e2 == 0 {
		assert Power2(e2) == 1;	
	} else {
		var e2min1 := e2 - 1;
		calc {
			Power2(e1 + e2);
			Power2(e1 + e2 - 1) * 2;
			Power2(e1 + e2min1) * 2;
			{ Lemma_Power2Adds(e1, e2min1); }
			Power2(e1) * Power2(e2min1) * 2;
			Power2(e1) * Power2(e2-1) * 2;
			calc {
				Power2(e2);
				Power2(e2-1) * 2;
			}
			Power2(e1) * Power2(e2);
		}
		assert Power2(e1 + e2) == Power2(e1) * Power2(e2);
	}
}

