method stoogeSort(a: array<int>, left: int, right: int)
  requires a != null;
  requires 0 <= left <= right < a.Length;
  modifies a;
  decreases right - left;
	ensures a[left] <= old(a[left])
	ensures forall i :: 0 <= i < left || right < i < a.Length ==> a[i] == old(a[i])
	ensures forall i :: left <= i <= right ==> exists j :: left <= j <= right && a[i] == old(a[j])
	ensures forall i, j :: left <= i < j <= right ==> a[i] <= a[j]
{
  if (a[left] > a[right])
  {
    var tmp := a[left];
    a[left] := a[right];
    a[right] := tmp;
  }
  if (left + 1 >= right)
  {
    return;
	}
  stoogeSort(a, left, right - 1);
  stoogeSort(a, left + 1, right);
  stoogeSort(a, left, right - 1);
}

function somatorio(a: array?<int>, n:int) : int
  requires a != null;
  requires n >= 0 && n <= a.Length;
  decreases n;
  reads a;
{
  if (n == 0) then
    0
  else
    somatorio(a, n-1) + a[n-1]
}
method somatorioArray(a: array?<int>) returns (result: int)
  requires a != null;
  ensures result == somatorio(a, a.Length);
{
  result := 0;
  var i := 0;
  while i < a.Length
    invariant i >= 0 && i <= a.Length;
    invariant result == somatorio(a, i);
    decreases a.Length - i;
  {
    result := result + a[i];
    i := i + 1;
  }
}

