method BinarySearch(a: array<int>, value: int) returns (index: int)
  requires a != null && a.Length > 0;
  requires forall i, j :: 0 <= i < j < a.Length ==> a[i] <= a[j];
  ensures -1 <= index < a.Length;
  ensures 0 <= index < a.Length ==> a[index] == value;
  ensures index == -1 ==> forall k :: 0 <= k < a.Length ==> a[k] != value;
{
  var low, high := 0, a.Length;
  while low < high
    invariant 0 <= low <= high <= a.Length;
    invariant forall k :: 0 <= k < a.Length && !(low <= k < high) ==> a[k] != value;
  {
    var mid:int := (low + high) / 2;
    if a[mid] < value
    {
      low := mid + 1;
    }
    else if value < a[mid]
    {
      high := mid;
    }
    else
    {
      return mid;
    }
  }
  return -1;
}

method BubbleSort(a:array<int>)
  requires a != null;
  ensures forall k:: forall l:: 0 <= k < l < a.Length ==> a[k] <= a[l]; 
  modifies a;
{
  var i:int := a.Length - 1;
  while(i > 0)
    invariant -1 <= i <= a.Length - 1;
    invariant a.Length == 0 <==>  i == -1;
    invariant forall k:: forall l:: i <= k < l < a.Length ==> a[k] <= a[l];
    invariant forall k:: forall l:: 0 <= k <= i && i+1 <= l <= a.Length - 1 ==>  a[k] <= a[l]; 
  {
    var j:int := 0;
    while (j < i)
      invariant 1 <= i <= a.Length -1 && 0 <= j <= i;
      invariant forall k:: forall l:: i <= k < l < a.Length ==>  a[k] <= a[l];
      invariant forall k:: forall l:: 0 <= k <= i && i+1 <= l <= a.Length - 1 ==>  a[k] <= a[l];
      invariant forall k:: 0 <= k < j ==>  a[k] <= a[j];
    {
      if (a[j] > a[j + 1]) {
        var t:int := a[j];
        a[j] := a[j + 1];
        a[j + 1] := t;
      }
      j := j + 1;
    }
    i := i - 1;
  }
}

