predicate divides(f:nat, i:nat)
  requires 1<=f
{
  i % f == 0
}
predicate IsPrime(i:nat)
{
  && 1<i
  && ( forall f :: 1 < f < i ==> !divides(f, i) )
}
method test_prime(i:nat) returns (result:bool)
  requires 1<i
  ensures result == IsPrime(i)
{
  var f := 2;
  while (f < i)
    invariant forall g :: 1 < g < f ==> !divides(g, i)
  {
    if i % f == 0 {
      assert divides(f, i);
      return false;
    }
    f := f + 1;
  }
  return true;
}
method Main()
{
  var a := test_prime(3);
  assert a;
  var b := test_prime(4);
  assert divides(2, 4);
  assert !b;
  var c := test_prime(5);
  assert c;
}

function fib(n: nat): nat
{
    if n == 0 then 0 else
    if n == 1 then 1 else
        fib(n - 1) + fib(n - 2)
}
method ComputeFib(n: nat) returns (b: nat)
    ensures b == fib(n)
{
    var i: int := 0;
    var a := 1;
        b := 0;
    while i < n
        invariant 0 <= i <= n
        invariant b == fib(i)
        invariant i >= 1 ==> a == fib(i - 1)
        invariant i < 1 ==> a == 1
    {
        a, b := b, a + b;
        i := i + 1;
    }
}

predicate elementosDistintos(s: seq<int>)
{
    forall i :: 0 <= i < |s| ==> forall j :: 0 <= j < |s| && j != i ==> s[i] != s[j]
}
class Conjunto{
    var c : seq<nat>
    constructor()
    ensures |c| == 0
    ensures elementosDistintos(c);
    {
        c := [];
    }
    method size()  returns (s:nat)
    ensures s == |c|
    {
        s := |c|;
    }
    method contains(v : nat) returns (b:bool)
    ensures b <==> v in c
    {
        b := v in c;
    }
    method add(v : nat) returns (b:bool)
    modifies this
    ensures v in c
    ensures old(v !in c) ==> b
    ensures old(v in c) ==> !b
    ensures forall e :: e in c && e != v ==> e in c
    ensures old(v !in c) && b ==> v in c
    ensures b ==> old(|c|) + 1 == |c|
    ensures !b ==> old(|c|) == |c|
    ensures forall e :: (old(!( e in c)) && (e != v) ==> e !in c)
    ensures old(|c|) == |c| ==> (b == false)
    {
        b := true;
        var x := contains(v);
        if(x){
            b:=false;
        }else{
            c := c + [v];
            b := true;
        }
    }
    method inter(b : Conjunto) returns (d: Conjunto)
    ensures forall e :: e in d.c <==> (e in c && e in b.c)
    ensures elementosDistintos(d.c)
    {
        d := new Conjunto();
        var aux := 0;
        var x : bool;
        d.c := [];
        while (aux < |c|)
        invariant aux <= |c|
        invariant forall e :: e in c[..aux] && e in b.c ==> e in d.c 
        invariant forall e :: e in d.c ==> (e in c && e in b.c)
        invariant forall i, j :: 0 <= i < |d.c|  && 0 <= j < |d.c| && i != j ==> d.c[i] != d.c[j]
        decreases |c| - aux
        {
            x := c[aux] in b.c && c[aux] !in d.c;
            if (x) {
                d.c := d.c +  [c[aux]];
            }
            aux := aux + 1;
        }
    }
  method union(b : Conjunto) returns (d: Conjunto)
    ensures forall e :: (e in c || e in b.c) <==> e in d.c
    ensures |d.c| <= |c + b.c|
    {
        d := new Conjunto();
        d.c := c;
        var aux := 0;
        var x : bool;
        while (aux < |b.c|)
        invariant aux <= |b.c|
        invariant forall e :: e in c ==> e in d.c
        invariant forall e :: e in b.c[..aux] ==> e in d.c
        invariant forall e :: e in d.c ==> (e in c || e in b.c)
        invariant |d.c| <= |c + b.c[..aux]|
        decreases |b.c| - aux
        {
            x := b.c[aux] !in d.c;
            if (x) {
                d.c := d.c + [b.c[aux]];
            }
            aux := aux + 1;
        }
    }
    method diff(b : Conjunto) returns (d: Conjunto)
    ensures forall e :: (e in c && e !in b.c) <==> e in d.c
    ensures elementosDistintos(d.c)
    {
        d := new Conjunto();
        d.c := [];
        var aux := 0;
        var x : bool;
        while (aux < |c|)
        invariant aux <= |c|
        invariant forall e :: e in c[..aux] && e !in b.c <==> (e in d.c)
        invariant forall i, j :: 0 <= i < |d.c|  && 0 <= j < |d.c| && i != j ==> d.c[i] != d.c[j]
        decreases |c| - aux
        {
            x := c[aux] !in b.c && c[aux] !in d.c;
            if (x) {
                d.c := d.c + [c[aux]];
            }
            aux := aux + 1;
        }
    }
}
