  ensures Sum(n, Fib) == Fib(n + 2) - Fib(1) 
{
}
lemma {:induction false} FibSumManual(n: nat)
  ensures Sum(n, Fib) == Fib(n + 2) - Fib(1) 
{
  if n == 0 {
  } else {
    calc {
      Sum(n, Fib);
    ==  
      Fib(n) + Sum(n - 1, Fib);
    ==  { FibSumManual(n - 1); }
      Fib(n) + Fib(n - 1 + 2) - Fib(1);
    ==
      Fib(n) + Fib(n + 1) - Fib(1);
    ==  
      Fib(n + 2) - Fib(1);
    }
    assert Sum(n, Fib) == Fib(n + 2) - Fib(1); 
    assert Sum(n, Fib) == Fib(n + 2) - Fib(1); 
    assert Sum(n, Fib) == Sum(n, Fib);
  }
  assert Sum(n, Fib) == Fib(n + 2) - Fib(1); 
}

