function fib(n : nat ) : nat
  decreases n
{
    if n < 2 then n else fib(n - 2) + fib(n - 1)
}
method computeFib (n : nat) returns (x : nat)
    requires n > 0 
    ensures x == fib(n)
{
    var i := 0;
    x := 0;
    var y := 1;
    while  i < n 
        invariant 0 <= i <= n && x == fib(i) && y == fib(i+1) 
        decreases n - i
    {
        x, y := y, x + y; 
        i := i + 1;
    }
}
method testComputeFib() {
    var f := computeFib(3);
    assert f == 2;
}

method only_once<T(==)>(a: array<T>, key: T) returns (b:bool)
  ensures (multiset(a[..])[key] ==1 ) <==> b
{
  var i := 0;
  b := false;
  var keyCount := 0;
  while i < a.Length
    invariant 0 <= i <= a.Length
    invariant multiset(a[..i])[key] == keyCount
    invariant b <==> (keyCount == 1)
  {
    if (a[i] == key)
    {
      keyCount := keyCount + 1;
    }
    if (keyCount == 1)
    { b := true; }
    else
    { b := false; }
    i := i + 1;
  }
  assert a[..a.Length] == a[..];
}

