method sqrt(n : nat) returns (r: int)
  ensures r * r <= n
  ensures forall i :: 0 <= i < r ==> square(i) < r * r
{
    var i := 0; 
    r := 0;
    while i * i <= n
      invariant r * r <= n
      invariant forall k :: 0 <= k < r ==> square(k) < r * r 
      decreases n - i
    {
      r := i;
      i := i + 1;
    }
    return r;
