method Max(a:array<nat>)returns(m:int)
ensures a.Length > 0 ==> forall k :: 0<=k<a.Length ==> m >= a[k]
ensures a.Length == 0 ==> m == -1
ensures a.Length > 0 ==> m in a[..] 
{
    if(a.Length == 0){
        return -1;
    }
    assert a.Length > 0;
    var i := 0;
    m := a[0];
    assert m in a[..]; 
    while(i < a.Length)
    invariant 0<=i<=a.Length
    invariant forall k :: 0<=k<i ==> m >= a[k]
    invariant m in a[..] 
    {
        if(a[i] >= m){
            m:= a[i];
        }
        i := i+1;
    }
    assert m in a[..]; 
}
