method M0(lo: int, hi: int) returns (s: int, t: int)
  requires lo <= hi
{
  s, t := 0, 0;
  for i := lo to hi {
    s := s + i;
  }
  for i := hi downto lo {
    t := t + i;
  }
}
method M1(lo: int, hi: int) returns (s: int, t: int)
  requires lo <= hi
{
  s, t := 0, 0;
  for i := lo to *
    invariant i <= hi
    decreases hi - i
  {
    if i == hi {
      break;
    }
    s := s + i;
  }
  if lo < hi {
    for i := hi downto *
      invariant lo <= i
      decreases i - lo
    {
      if i == lo - 1 {
        break;
      }
      t := t + i;
    }
  }
}
