method Mul(a: nat, b: nat) {
  var c := 0;
  var aa := a;
  for _ := 0 to aa {
    var bb := b;
    for _ := bb downto 0 {
      c, bb, aa := c + 1, bb + 3, aa + 2;
    }
    aa := aa + 5;
  }
  print a, " * ", b, " == ", c, "\n";
}
method Add(a: nat, b: nat) {
  var s := 0;
  label Outer0:
  label Outer1:
  for _ := 0 to 7 {
    for i := 0 to *
      invariant i <= a
      decreases a - i
    {
      if i == a {
        if i % 2 == 0 {
          break Outer0;
        } else {
          break Outer1;
        }
      }
      if a < i {
        break; 
      }
      s := s + 1;
    }
    s := 77; 
  }
  for _ := 2 to 3 {
    label Inner:
    for i: int := b downto *
      invariant 0 <= i
      decreases i
    {
      if i < 0 {
        break Inner;
      }
      s := s + 1;
    }
  }
  print a, " + ", b, " == ", s, "\n";
}

class CircularMemory
{
  var cells : array<int>;
  var read_position : int;
  var write_position : int;
  var isFlipped : bool;
  constructor Init(cap : int)
  ensures cap >=0
  ensures Valid()
  requires cap >0
  {
    cells := new int[cap];
    read_position, write_position := 0, 0;
    isFlipped := false;
  }
  predicate Valid()
    reads this
  {
    0 <= write_position < cells.Length &&
    0 <= read_position < cells.Length
  }
  predicate isEmpty()
    reads this
  {
  write_position == read_position
  }
  predicate isFull()
    reads this
  {
  write_position ==read_position-1 
  }
 method Read() returns (isSuccess : bool, content : int)
    modifies this
    requires Valid();
    ensures  Valid();
    ensures  isSuccess ==> content == old(cells[read_position]);
    ensures !isSuccess ==> content==0 && read_position == old(read_position);
  {
    if(write_position==read_position){
       isSuccess := false;
        content := 0;
    }
    else{
        isSuccess :=true;
        content := cells[read_position];
      if(isFlipped)
        {
            if(read_position == cells.Length-1){
                read_position:=0;
                isFlipped:=false;
            }
            else
             {
             read_position := read_position+1;
            }
        }
    else
    {
      if(read_position <cells.Length-1){
        read_position := read_position +1;
      }
      }
    }
  }
  method Write(input : int) returns (isSuccess : bool)
    modifies cells, this`write_position, this`isFlipped;
    requires Valid()
    ensures  Valid()
    ensures  isSuccess ==> cells[old(write_position)]==input;
    ensures !isSuccess ==> write_position == old(write_position)
  {
    if(read_position-1 == write_position)
    {
        isSuccess := false;
    } 
    else{
      if(isFlipped)
       {
             if(write_position >= read_position){
              isSuccess := false;
           } 
            else
            {
              cells[write_position]:= input;
              write_position:=write_position+1;
                isSuccess := true;
            }
        }
      else 
        {
          if(write_position < read_position){
          isSuccess := false;
        }else{
            isSuccess:=true;
            cells[write_position]:= input;
          if(write_position==cells.Length-1){
            write_position:=0;
            isFlipped:=true;          
          }
          else{
   write_position:=write_position +1;
          }
        }
      }
    }
  }
}

method LinearSearch(A:array<int>, key:int) returns (index:int)
  ensures 0 <= index ==> index < A.Length && A[index] == key
  ensures index < 0 ==> key !in A[..]
{
  var N := A.Length;
  var i := 0;
  while i < N
    invariant 0 <= i <= N
    invariant forall k :: 0 <= k < i ==> A[k] != key
    decreases N - i
  {
    if A[i] == key
    {
      return i;
    }
    i := i + 1;
  }
  return -1;
}

