function power(x: real, n: nat) : real
  decreases n
{
    if n == 0 then 1.0 else x * power(x, n-1)
}
method powerIter(x: real, n: nat) returns (p : real)
  ensures p == power(x, n)
{
    p := 1.0;
    var i := 0;
    while i < n
      decreases n - i
      invariant 0 <= i <= n && p == power(x, i)
    {
        p := p * x;
        i := i + 1;
    }
}
method powerOpt(x: real, n: nat) returns (p : real)
  ensures p == power(x, n);
  decreases n;
{
    if n == 0 {
        return 1.0;
    }
    else if n == 1 {
        return x;
    }
    else if n % 2 == 0 {
        distributiveProperty(x,  n/2, n/2); 
        var temp := powerOpt(x, n/2);
        return temp * temp;
    }
    else {
        distributiveProperty(x, (n-1)/2, (n-1)/2); 
        var temp := powerOpt(x, (n-1)/2);
        return temp * temp * x;
    } 
}
lemma {:induction false} distributiveProperty(x: real, a: nat, b: nat) 
  ensures power(x, a) * power(x, b)  == power(x, a + b) 
{
    if a == 0 {
        calc == {
            power(x, a) * power(x, b);
            power(x, 0) * power(x, b); 
            1.0 * power(x, b); 
            power(x, b); 
            power(x, a + b); 
        }
    }
    else {
        distributiveProperty(x, a-1, b); 
        calc == {
            power(x, a) * power(x, b);
            (x * power(x, a-1)) * power(x, b); 
            x * (power(x, a-1) * power(x, b)); 
            x * power(x, a + b - 1); 
            power(x, 1 + (a - 1 + b));
            power(x, a + b); 
        }
    }
}
method testPower() {
    var p1 := powerIter(2.0, 5);
    var p2 := powerOpt(2.0, 5);
    assert p1 == 32.0;
    assert p2 == 32.0;
}

method find (a : array <int>, key : int) returns ( index : int )
    ensures 0 <= index <= a.Length;
    ensures index < a.Length ==> a[index] == key;
{
    index := 0;
    assert 0 <= index <= a.Length; 
    while index < a.Length && a[index] != key 
        invariant 0 <= index <= a.Length;
        invariant forall i:: 0 <= i < index ==> a[i] != key;
        decreases a.Length - index;
    {
        ghost var Vzero := a.Length - index;
        assert index < a.Length && 0 <= index <= a.Length && a.Length - index == Vzero; 
        index := index + 1;
        assert 0 <= index <= a.Length && 0 <= a.Length - index < Vzero; 
    }
    assert index < a.Length ==> a[index] == key; 
}

predicate sorted(a:array<int>, min:int, max:int)
 requires 0<= min <= max <= a.Length
 reads a
 {
      forall j, k :: min <= j < k < max ==> a[j] <= a[k]
}
method FindMin(a: array<int>, from: int) returns (min: int)
requires a.Length >= 1
requires  0 <= from < a.Length
ensures  from <= min < a.Length
ensures forall k :: from <= k < a.Length ==> a[k] >= a[min]
{
    var i := from;
    min := from;
    while i < a.Length
	   decreases a.Length - i;
       invariant from <= i <= a.Length
       invariant from <= min < a.Length
       invariant forall k :: from <= k < i ==> a[k] >= a[min]
    {
        if a[i] <= a[min] {min := i;}      
        i := i+1;
     }
   return min;
}
method SelectionSort ( a: array<int> ) 
requires  a.Length >=1
modifies a
ensures sorted(a, 0, a.Length)
{ 
	   var min, current :=  0, 0;
	   while (current < a.Length) 
	   invariant 0 <= current <= a.Length
	   invariant sorted(a, 0, current)
	   invariant forall k, l :: 0 <= k < current <= l < a.Length ==> a[k] <= a[l];
	   {
	     min := FindMin (a, current);
	     a[min], a[current] := a[current], a[min];
	     current := current +1;
	   }
}

