    requires amount > 0
    ensures checking == old(checking) + amount
    modifies this`checking
  {
    checking := checking + amount;
  }
  method withdraw(amount:int)
    requires amount > 0 
    requires hasBalance(amount)
    ensures checking == old(checking) - amount
    modifies this`checking
  {
    checking := checking - amount;
  }
   method save(amount:int) 
    requires amount > 0
    requires hasDebt()
    modifies this`savings
  {
    savings := savings + amount;
  }
  method rescue(amount:int) 
    requires amount > 0
    ensures savings == old(savings) - amount
    modifies this`savings
  {
    savings := savings - amount;
  }
  function hasBalance(amount:int) : bool
    reads this
  {
    savings / 2 >= checking + amount
  }
  function hasDebt(): bool
    reads this
  {
    checking >= 0
  }
